class Curl {
    private $curlOptions = [];
    private $requestArguments = [];
    private $requestHeader = [];
    private $responseBody;
    private $responseHeader = [];
    private $responseHttpCode;
    private $responseClientError;
    private $responseClientInfo;
    private $logger;

    public function __construct() {
        $this->curlOptions = [
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_HEADER => true,
            CURLOPT_FOLLOWLOCATION => true,
            CURLOPT_TIMEOUT => 30,
            CURLOPT_CONNECTTIMEOUT => 10,
            CURLOPT_SSL_VERIFYPEER => false,
            CURLOPT_SSL_VERIFYHOST => false,
        ];
        $this->requestHeader = [
            'User-Agent' => 'Curl/PHP',
            'Content-Type' => 'application/json',
        ];
    }

    public function request($uri, $method = 'GET', $parameters = [], $headers = [], $multipart = false) {
        $effectiveHeaders = array_merge($this->requestHeader, $headers);
        $this->requestArguments = [
            'uri' => $uri,
            'method' => $method,
            'parameters' => $parameters,
            'headers' => $effectiveHeaders,
            'multipart' => $multipart,
        ];

        $curl = curl_init();
        $options = [];

        if (in_array($method, ['GET', 'HEAD'])) {
            if (!empty($parameters)) {
                $uri .= '?' . http_build_query($parameters);
            }
            $options[CURLOPT_HTTPGET] = true;
        } else {
            $options[CURLOPT_CUSTOMREQUEST] = $method;
            if (!empty($parameters)) {
                if ($multipart) {
                    $postData = http_build_query($parameters);
                } else {
                    $postData = json_encode($parameters);
                    $hasContentType = false;
                    foreach ($effectiveHeaders as $key => $value) {
                        if (strtolower($key) === 'content-type') {
                            $hasContentType = true;
                            break;
                        }
                    }
                    if (!$hasContentType) {
                        $effectiveHeaders['Content-Type'] = 'application/json';
                    }
                }
                $options[CURLOPT_POSTFIELDS] = $postData;
            }
        }

        $options[CURLOPT_URL] = $uri;
        $curlHeaders = $this->prepareRequestHeaders($effectiveHeaders);
        $options[CURLOPT_HTTPHEADER] = $curlHeaders;
        $options[CURLOPT_HEADERFUNCTION] = [$this, 'fetchResponseHeader'];
        $options += $this->curlOptions;

        curl_setopt_array($curl, $options);

        if ($this->logger) {
            $this->logger->log('Request: ' . $method . ' ' . $uri, $this->requestArguments);
        }

        $this->responseBody = curl_exec($curl);
        $this->responseClientError = curl_error($curl);
        $this->responseClientInfo = curl_getinfo($curl);
        $this->responseHttpCode = curl_getinfo($curl, CURLINFO_HTTP_CODE);

        curl_close($curl);

        if ($this->logger) {
            $this->logger->log('Response: ' . $this->responseHttpCode, [
                'headers' => $this->responseHeader,
                'body' => $this->responseBody,
                'error' => $this->responseClientError,
            ]);
        }

        return $this->responseBody;
    }

    public function getResponse() {
        return [
            'body' => $this->responseBody,
            'headers' => $this->responseHeader,
            'http_code' => $this->responseHttpCode,
            'client_error' => $this->responseClientError,
            'client_info' => $this->responseClientInfo,
            'request' => $this->requestArguments,
        ];
    }

    public function setCurlOptions(array $curlOptions) {
        $this->curlOptions = array_merge($this->curlOptions, $curlOptions);
    }

    public function setLogger($logger) {
        $this->logger = $logger;
    }

    public function getResponseBody() {
        return $this->responseBody;
    }

    public function getResponseHeader() {
        return $this->responseHeader;
    }

    public function getResponseHttpCode() {
        return $this->responseHttpCode;
    }

    public function getResponseClientError() {
        return $this->responseClientError;
    }

    public function getResponseClientInfo() {
        return $this->responseClientInfo;
    }

    public function getRequestArguments() {
        return $this->requestArguments;
    }

    private function fetchResponseHeader($curl, $header) {
        $len = strlen($header);
        $header = explode(':', $header, 2);
        if (count($header) === 2) {
            $name = trim($header[0]);
            $value = trim($header[1]);
            if (!array_key_exists($name, $this->responseHeader)) {
                $this->responseHeader[$name] = $value;
            } else {
                if (!is_array($this->responseHeader[$name])) {
                    $this->responseHeader[$name] = [$this->responseHeader[$name]];
                }
                $this->responseHeader[$name][] = $value;
            }
        } else {
            $this->responseHeader[] = trim($header[0]);
        }
        return $len;
    }

    private function prepareRequestHeaders($headers) {
        $curlHeaders = [];
        foreach ($headers as $name => $value) {
            $curlHeaders[] = $name . ': ' . $value;
        }
        return $curlHeaders;
    }
}

