class Client {
    public static function get($url, $headers = []) {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_HTTPGET, true);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        if (!empty($headers)) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }
        curl_setopt($ch, CURLOPT_USERAGENT, self::userAgent());
        return self::sendRequest($ch);
    }

    public static function delete($url, $headers = []) {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'DELETE');
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        if (!empty($headers)) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }
        curl_setopt($ch, CURLOPT_USERAGENT, self::userAgent());
        return self::sendRequest($ch);
    }

    public static function post($url, $body, $headers = []) {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        if (!empty($headers)) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }
        curl_setopt($ch, CURLOPT_USERAGENT, self::userAgent());
        return self::sendRequest($ch);
    }

    public static function multipartPost($url, $fields, $name, $fileName, $fileBody, $mimeType = null, $headers = []) {
        $tempHandle = tmpfile();
        fwrite($tempHandle, $fileBody);
        $meta = stream_get_meta_data($tempHandle);
        $tempPath = $meta['uri'];

        $curlFile = new \CURLFile($tempPath, $mimeType ?: '', $fileName);
        $fields[$name] = $curlFile;

        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $fields);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        if (!empty($headers)) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }
        curl_setopt($ch, CURLOPT_USERAGENT, self::userAgent());

        $response = self::sendRequest($ch);

        fclose($tempHandle);

        return $response;
    }

    private static function sendRequest($ch) {
        if (!is_resource($ch) || get_resource_type($ch) !== 'curl') {
            throw new \InvalidArgumentException('Invalid cURL handle.');
        }

        $responseText = curl_exec($ch);
        if ($responseText === false) {
            $errno = curl_errno($ch);
            $error = curl_error($ch);
            curl_close($ch);
            throw new \RuntimeException(sprintf('cURL error %d: %s', $errno, $error));
        }

        $statusCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headersRaw = substr($responseText, 0, $headerSize);
        $body = substr($responseText, $headerSize);

        curl_close($ch);

        $headers = self::parseHeaders($headersRaw);

        return new Response($statusCode, $headers, $body);
    }

    public static function userAgent() {
        $sdkVersion = 'ClientPHP/1.0';
        $systemInfo = php_uname('s n r v m');
        $phpVersion = PHP_VERSION;
        return "$sdkVersion ($systemInfo) PHP/$phpVersion";
    }

    public static function parseHeaders($raw) {
        $headers = [];
        foreach (explode("\r\n", $raw) as $line) {
            $line = trim($line);
            if ($line === '') continue;
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $key = self::ucwordsHyphen(trim($key));
                $value = trim($value);
                if (isset($headers[$key])) {
                    if (!is_array($headers[$key])) {
                        $headers[$key] = [$headers[$key]];
                    }
                    $headers[$key][] = $value;
                } else {
                    $headers[$key] = $value;
                }
            } else {
                $headers['Status'] = $line;
            }
        }
        return $headers;
    }

    public static function escapeQuotes($str) {
        return addcslashes($str, '"\\');
    }

    public static function ucwordsHyphen($str) {
        return ucwords(strtolower($str), '-');
    }
}

