        $this->accessKeyId = $accessKeyId;
        $this->accessSecrec = $accessSecrec;
        $this->DomainName = "";
        $this->Host = "";
        $this->Path = "";
        if (!empty($domain)) {
            $parsed = self::getDomain($domain);
            $this->DomainName = $parsed['domain'];
            $this->Host = $parsed['host'];
        }
    }

    public static function getDomain($domain) {
        $parts = explode('.', $domain);
        if (count($parts) < 2) {
            return ['domain' => $domain, 'host' => ''];
        }
        $lastTwo = array_slice($parts, -2);
        $domainName = implode('.', $lastTwo);
        $host = implode('.', array_slice($parts, 0, -2));
        return ['domain' => $domainName, 'host' => $host];
    }

    private function curl($url, $headers = [], $data = '', $method = 'get') {
        $defaultHeaders = [
            'Authorization: sso-key ' . $this->accessKeyId . ':' . $this->accessSecrec,
            'Content-Type: application/json',
            'Accept: application/json'
        ];
        $useHeaders = empty($headers) ? $defaultHeaders : array_merge($defaultHeaders, $headers);

        $ch = curl_init();
        $options = [
            CURLOPT_URL => $url,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_HTTPHEADER => $useHeaders,
            CURLOPT_CUSTOMREQUEST => strtoupper($method)
        ];

        if (in_array(strtolower($method), ['post', 'put'])) {
            $options[CURLOPT_POSTFIELDS] = $data;
            if (strtolower($method) === 'put') {
                $options[CURLOPT_CUSTOMREQUEST] = 'PUT';
            }
        }

        curl_setopt_array($ch, $options);
        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        $decoded = json_decode($response, true);
        return ['response' => $decoded, 'code' => $httpCode];
    }

    public function getDomains() {
        $result = $this->curl('https://api.godaddy.com/v1/domains', [], '', 'get');
