class Response {
    public $statusCode;
    public $headers;
    public $body;
    public $timing;

    public function __construct($statusCode, $headers, $body, $timing) {
        $this->statusCode = $statusCode;
        $this->headers = $headers;
        $this->body = $body;
        $this->timing = $timing;
    }
}

class Client {
    public static function get(string $url, array $headers = []): Response {
        return self::sendRequest('GET', $url, $headers, null);
    }

    public static function delete(string $url, array $headers = []): Response {
        return self::sendRequest('DELETE', $url, $headers, null);
    }

    public static function post(string $url, $body, array $headers = []): Response {
        return self::sendRequest('POST', $url, $headers, $body);
    }

    public static function put(string $url, $body, array $headers = []): Response {
        return self::sendRequest('PUT', $url, $headers, $body);
    }

    public static function multipartPost(
        string $url,
        array $fields,
        string $name,
        string $fileName,
        string $fileBody,
        string $mimeType = null,
        array $headers = []
    ): Response {
        $boundary = '---------------------' . md5(microtime(true));
        $headers['Content-Type'] = 'multipart/form-data; boundary=' . $boundary;
        $body = '';

        foreach ($fields as $fieldName => $value) {
            $body .= "--$boundary\r\n";
            $body .= 'Content-Disposition: form-data; name="' . self::escapeQuotes($fieldName) . "\"\r\n\r\n";
            $body .= "$value\r\n";
        }

        $body .= "--$boundary\r\n";
        $body .= 'Content-Disposition: form-data; name="' . self::escapeQuotes($name) . '"; filename="' . self::escapeQuotes($fileName) . "\"\r\n";
        if ($mimeType) {
            $body .= "Content-Type: $mimeType\r\n\r\n";
        } else {
            $body .= "\r\n";
        }
        $body .= "$fileBody\r\n";
        $body .= "--$boundary--\r\n";

        return self::post($url, $body, $headers);
    }

    private static function sendRequest(string $method, string $url, array $headers, $body = null): Response {
        $ch = curl_init();

        curl_setopt_array($ch, [
            CURLOPT_URL            => $url,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_HEADER         => true,
            CURLOPT_FOLLOWLOCATION => true,
            CURLOPT_MAXREDIRS      => 10,
            CURLOPT_TIMEOUT        => 30,
            CURLOPT_CONNECTTIMEOUT => 30,
            CURLOPT_USERAGENT      => self::userAgent(),
            CURLOPT_HTTPHEADER     => array_map(fn($k, $v) => "$k: $v", array_keys($headers), $headers),
        ]);

        switch (strtoupper($method)) {
            case 'GET':
                curl_setopt($ch, CURLOPT_HTTPGET, true);
                break;
            case 'POST':
                curl_setopt_array($ch, [CURLOPT_POST => true, CURLOPT_POSTFIELDS => $body]);
                break;
            case 'PUT':
                curl_setopt_array($ch, [CURLOPT_CUSTOMREQUEST => 'PUT', CURLOPT_POSTFIELDS => $body]);
                break;
            case 'DELETE':
                curl_setopt_array($ch, [CURLOPT_CUSTOMREQUEST => 'DELETE', CURLOPT_POSTFIELDS => $body]);
                break;
            default:
                curl_setopt_array($ch, [CURLOPT_CUSTOMREQUEST => $method, CURLOPT_POSTFIELDS => $body]);
        }

        $startTime = microtime(true);
        $response = curl_exec($ch);
        $endTime = microtime(true);

        if ($response === false) {
            throw new \RuntimeException('cURL error: ' . curl_error($ch));
        }

        $statusCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $rawHeaders = substr($response, 0, $headerSize);
        $bodyContent = substr($response, $headerSize);

        curl_close($ch);

        return new Response($statusCode, self::parseHeaders($rawHeaders), $bodyContent, $endTime - $startTime);
    }

    public static function userAgent(): string {
        return "MySDK/1.0.0 (" . php_uname('s') . " " . php_uname('r') . ") PHP/" . PHP_VERSION;
    }

    private static function parseHeaders(string $raw): array {
        $headers = [];
        foreach (explode("\r\n", $raw) as $line) {
            if (($pos = strpos($line, ': ')) !== false) {
                $key = self::ucwordsHyphen(substr($line, 0, $pos));
                $headers[$key] = substr($line, $pos + 2);
            }
        }
        return $headers;
    }

    private static function escapeQuotes(string $str): string {
        return addcslashes($str, '"\\');
    }

    private static function ucwordsHyphen(string $str): string {
        return implode('-', array_map('ucfirst', explode('-', strtolower($str))));
    }
}

