    }
}

class Downloader {
    static function start($url,$saveFile,$timeout = 10) {
        $dataFile = $saveFile . '.download.cfg';
        $saveTemp = $saveFile . '.downloading';

        if(is_array($url)){
            $fileHeader = $url;
        }else{
            $fileHeader = url_header($url);
        }
        $url = $fileHeader['url'];
        if(!$url){
            return array('code'=>false,'data'=>'url error!');
        }
        if(!$fileHeader['supportRange'] || $fileHeader['length'] == 0 ){
            @unlink($saveTemp);@unlink($saveFile);
            $result = self::fileDownloadFopen($url,$saveFile,$fileHeader['length']);
            if($result['code']) {
                return $result;
            }else{
                @unlink($saveTemp);@unlink($saveFile);
                $result = self::fileDownloadCurl($url,$saveFile,false,0,$fileHeader['length']);
                @unlink($saveTemp);@unlink($saveFile);
                return $result;
            }
        }

        $existsLength  = is_file($saveTemp) ? filesize($saveTemp) : 0;
        $contentLength = intval($fileHeader['length']);
        if( file_exists($saveTemp) && time() - filemtime($saveTemp) < 3) {
            return array('code'=>false,'data'=>'downloading');
        }

        $existsData = array();
        if(is_file($dataFile)){
            $tempData = file_get_contents($dataFile);
            $existsData = json_decode($tempData, 1);
        }
        if( file_exists($saveFile) && $contentLength == filesize($saveFile)){
            @unlink($saveTemp);
            @unlink($dataFile);
            return array('code'=>true,'data'=>'exist');
        }

        if ($existsData['length'] != $contentLength) {
            $existsData = array('length' => $contentLength);
        }
        if($existsLength > $contentLength){
            @unlink($saveTemp);
        }
        file_put_contents($dataFile, json_encode($existsData));
        $result = self::fileDownloadCurl($url,$saveFile,true,$existsLength,$contentLength);
        if($result['code']){
            @unlink($dataFile);
        }
        return $result;
    }

    static function fileDownloadFopen($url, $fileName,$headerSize=0){
        @ini_set('user_agent','Mozilla/5.0 (Windows NT 6.2; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/27.0.1453.94 Safari/537.36');

        $fileTemp = $fileName.'.downloading';
        @set_time_limit(0);
        @unlink($fileTemp);
        if ($fp = @fopen ($url, "rb")){
            if(!$downloadFp = @fopen($fileTemp, "wb")){
                return array('code'=>false,'data'=>'open_downloading_error');
            }
            while(!feof($fp)){
                if(!file_exists($fileTemp)){
                    fclose($downloadFp);
                    return array('code'=>false,'data'=>'stoped');
                }
                clearstatcache();
                if( $headerSize>0 && $headerSize==get_filesize(iconv_system($fileTemp))){
                    break;
                }
                fwrite($downloadFp, fread($fp, 1024 * 8 ), 1024 * 8);
            }
            fclose($downloadFp);
            fclose($fp);
            self::checkGzip($fileTemp);
            if(!@rename($fileTemp,$fileName)){
                usleep(round(rand(0,1000)*50));
                @unlink($fileName);
                $res = @rename($fileTemp,$fileName);
                if(!$res){
                    return array('code'=>false,'data'=>'rename error![open]');
                }
            }
            return array('code'=>true,'data'=>'success');
        }else{
            return array('code'=>false,'data'=>'url_open_error');
        }
    }

    static function fileDownloadCurl($url, $fileName,$supportRange=false,$existsLength=0,$length=0){
        $fileTemp = $fileName.'.downloading';
        @set_time_limit(0);
        if ($fp = @fopen ($fileTemp, "a")){
            $ch = curl_init($url);
            if($supportRange){
                curl_setopt($ch, CURLOPT_RANGE, $existsLength."-");
            }
            curl_setopt($ch, CURLOPT_FILE, $fp);
            curl_setopt($ch, CURLOPT_REFERER,get_url_link($url));
            curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);
            curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);
            curl_setopt($ch, CURLOPT_USERAGENT,'Mozilla/5.0 (Windows NT 6.2; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/27.0.1453.94 Safari/537.36');

            $res = curl_exec($ch);
            curl_close($ch);
            fclose($fp);

            $filesize = get_filesize(iconv_system($fileTemp));
            if($filesize < $length && $length!=0){
                return array('code'=>false,'data'=>'downloading');
            }
            if($res && filesize($fileTemp) != 0){
                self::checkGzip($fileTemp);
                if(!@rename($fileTemp,$fileName)){
                    @unlink($fileName);
                    $res = @rename($fileTemp,$fileName);
                    if(!$res){
                        return array('code'=>false,'data'=>'rename error![curl]');
                    }
                }
                return array('code'=>true,'data'=>'success');
            }
            return array('code'=>false,'data'=>'curl exec error!');
        }else{
            return array('code'=>false,'data'=>'file create error');
        }
    }

    static function checkGzip($file){
        $char = "\x1f\x8b";
        $str  = file_sub_str($file,0,2);
        if($char != $str) return;

        ob_start();   
        readgzfile($file);   
        $out = ob_get_clean();
        file_put_contents($file,$out);
    }
}

namespace Hhxsv5\LaravelS\Components\HttpClient;

use Swoole\Coroutine\Http\Client as CoroutineClient;

class SimpleHttpClient
{
    protected $curlOptions = [
        CURLOPT_HEADER         => true,
        CURLOPT_FOLLOWLOCATION => true,
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_MAXREDIRS      => 3,
        CURLOPT_TIMEOUT        => 5,
        CURLOPT_CONNECTTIMEOUT => 3,
    ];

    public function httpGet($url, array $options)
    {
        $handle = curl_init();
        $finalOptions = [
                CURLOPT_URL     => $url,
                CURLOPT_HTTPGET => true,
            ] + $this->curlOptions;
        if (isset($options['timeout'])) {
            $finalOptions[CURLOPT_TIMEOUT] = $options['timeout'];
        }
        curl_setopt_array($handle, $finalOptions);
        $responseStr = curl_exec($handle);
        $errno = curl_errno($handle);
        $errmsg = curl_error($handle);
        if ($errno !== 0 || $errmsg !== '') {
            curl_close($handle);
            $msg = sprintf('Failed to send Http request(%s), errcode=%d, errmsg=%s', $url, $errno, $errmsg);
            throw new \RuntimeException($msg, $errno);
        }

        $headerSize = curl_getinfo($handle, CURLINFO_HEADER_SIZE);
        $statusCode = curl_getinfo($handle, CURLINFO_HTTP_CODE);
        curl_close($handle);

        $header = substr($responseStr, 0, $headerSize);
        $body = substr($responseStr, $headerSize);
        $lines = explode("\n", $header);
        array_shift($lines);

        $headers = [];
        foreach ($lines as $part) {
            $middle = explode(':', $part);
            $key = trim($middle[0]);
            if ($key === '') {
                continue;
            }
            if (isset($headers[$key])) {
                $headers[$key] = (array)$headers[$key];
                $headers[$key][] = isset($middle[1]) ? trim($middle[1]) : '';
            } else {
                $headers[$key] = isset($middle[1]) ? trim($middle[1]) : '';
            }
        }
        return ['statusCode' => $statusCode, 'headers' => $headers, 'body' => $body];
    }
}

use Think\Upload\Driver\Bcs\BaiduBCS;

class Bcs
{
    private $rootPath;
    const DEFAULT_URL = 'bcs.duapp.com';
    private $error = '';
    public $config = array(
        'AccessKey'=> '',
        'SecretKey'=> '',
        'bucket'   => '',
        'rename'   => false,
        'timeout'  => 3600,
    );
    public $bcs = null;

    public function __construct($config)
    {
        $this->config = array_merge($this->config, $config);
        $bcsClass = dirname(__FILE__). "/Bcs/bcs.class.php";
        if(is_file($bcsClass))
            require_once($bcsClass);
        $this->bcs = new BaiduBCS($this->config['AccessKey'], $this->config['SecretKey'], self::DEFAULT_URL);
    }

    private function request($path, $method, $headers = null, $body = null)
    {
        $ch = curl_init($path);
        $_headers = array('Expect:');
        if (!is_null($headers) && is_array($headers)){
            foreach($headers as $k => $v) {
                array_push($_headers, "{$k}: {$v}");
            }
        }

        $length = 0;
        $date = gmdate('D, d M Y H:i:s \G\M\T');
        if (!is_null($body)) {
            if(is_resource($body)){
                fseek($body, 0, SEEK_END);
                $length = ftell($body);
                fseek($body, 0);
                array_push($_headers, "Content-Length: {$length}");
                curl_setopt($ch, CURLOPT_INFILE, $body);
                curl_setopt($ch, CURLOPT_INFILESIZE, $length);
            } else {
                $length = @strlen($body);
                array_push($_headers, "Content-Length: {$length}");
                curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
            }
        } else {
            array_push($_headers, "Content-Length: {$length}");
        }

        array_push($_headers, "Date: {$date}");
        curl_setopt($ch, CURLOPT_HTTPHEADER, $_headers);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->config['timeout']);
        curl_setopt($ch, CURLOPT_HEADER, 1);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 0);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

        if ($method == 'PUT' || $method == 'POST') {
            curl_setopt($ch, CURLOPT_POST, 1);
        } else {
            curl_setopt($ch, CURLOPT_POST, 0);
        }

        if ($method == 'HEAD') {
            curl_setopt($ch, CURLOPT_NOBODY, true);
        }

        $response = curl_exec($ch);
        $status = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        list($header, $body) = explode("\r\n\r\n", $response, 2);

        if ($status == 200) {
            if ($method == 'GET') {
                return $body;
            } else {
                $data = $this->response($header);
                return count($data) > 0 ? $data : true;
            }
        } else {
            $this->error($header);
            return false;
        }
    }

    private function response($text)
    {
        $items = json_decode($text, true);
        return $items;
    }

    private function error($header)
    {
        list($status, $stash) = explode("\r\n", $header, 2);
        list($v, $code, $message) = explode(" ", $status, 3);
        $message = is_null($message) ? 'File Not Found' : "[{$status}]:{$message}";
        $this->error = $message;
    }
}

class AlipayService
