    private $url;
    private $useragent;
    private $success;
    private $headers;
    private $body;
    private $status_code;
    private $redirects;
    private $error;
    private $method;
    private $permanent_url;
    private $permanentUrlMutable;

    public function __construct($url, $timeout = 10, $redirects = 5, $headers = null, $useragent = null, $force_fsockopen = false, $curl_options = []) {
        $this->permanent_url = $url;
        $this->redirects = 0;
        $this->useragent = $useragent ?: 'SimplePie';
        $this->method = 0;
        $this->permanentUrlMutable = true;
        $this->error = '';
        $this->headers = [];
        $this->body = '';
        $this->status_code = 0;
        $this->success = false;

        $currentUrl = $url;
        $redirectsRemaining = $redirects;

        do {
            $ch = curl_init();
            if (!$ch) {
                $this->error = 'Could not initialize cURL session.';
                return;
            }

            curl_setopt_array($ch, [
                CURLOPT_URL => $currentUrl,
                CURLOPT_RETURNTRANSFER => true,
                CURLOPT_HEADER => true,
                CURLOPT_TIMEOUT => $timeout,
                CURLOPT_FOLLOWLOCATION => false,
                CURLOPT_USERAGENT => $this->useragent
            ]);

            if ($headers !== null) {
                curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
            }

            foreach ($curl_options as $option => $value) {
                curl_setopt($ch, $option, $value);
            }

            $response = curl_exec($ch);
            $curlErrno = curl_errno($ch);
            $curlError = curl_error($ch);
            $info = curl_getinfo($ch);
            curl_close($ch);

            if ($curlErrno !== 0) {
                $this->error = "cURL error ({$curlErrno}): {$curlError}";
                return;
            }

            if (strpos($response, "\r\n\r\n") === false) {
                $this->error = 'Empty response';
                return;
            }

            list($headersPart, $body) = explode("\r\n\r\n", $response, 2);
            $this->body = $body;

            $headerLines = explode("\r\n", $headersPart);
            $statusLine = array_shift($headerLines);
            preg_match('/^HTTP\/\d\.\d\s+(\d+)/', $statusLine, $matches);
            $this->status_code = $matches ? (int)$matches[1] : 0;

            $headers = [];
            foreach ($headerLines as $line) {
                $line = trim($line);
                if (!$line || strpos($line, ':') === false) continue;
                list($name, $value) = explode(':', $line, 2);
                $name = trim($name);
                $value = trim($value);
                $headers[$name] = $value;
            }
            $this->headers = $headers;

            $isRedirect = false;
            if ($redirectsRemaining > 0 && in_array($this->status_code, [301, 302, 303, 307, 308]) && isset($this->headers['Location'])) {
                $redirectsRemaining--;
                $this->redirects++;
                $location = $this->headers['Location'];
                $currentUrl = $this->resolveUrl($currentUrl, $location);
                $isRedirect = true;
            }

            if (!$isRedirect) {
                $this->url = $currentUrl;
                $this->permanentUrlMutable = false;
                $this->success = ($this->status_code >= 200 && $this->status_code < 300);
                break;
            }
        } while (true);
    }

    private function resolveUrl($baseUrl, $relativeUrl) {
        if (parse_url($relativeUrl, PHP_URL_SCHEME) !== null) return $relativeUrl;
        $urlParts = parse_url($baseUrl);
        $scheme = isset($urlParts['scheme']) ? $urlParts['scheme'] . '://' : '';
        $host = isset($urlParts['host']) ? $urlParts['host'] : '';
        $port = isset($urlParts['port']) ? ':' . $urlParts['port'] : '';
        $user = isset($urlParts['user']) ? $urlParts['user'] : '';
        $pass = isset($urlParts['pass']) ? ':' . $urlParts['pass'] : '';
        $auth = ($user || $pass) ? "$user$pass@" : '';
        $path = isset($urlParts['path']) ? $urlParts['path'] : '';
        $query = isset($urlParts['query']) ? '?' . $urlParts['query'] : '';

        if ($relativeUrl[0] === '/') {
            $path = $relativeUrl;
        } else {
            $path = dirname($path) . '/' . $relativeUrl;
        }

        $path = preg_replace_callback('|(\.\./?)|', function ($m) use (&$path) {
            return '';
        }, $path);
        $path = str_replace('/./', '/', $path);
        return "$scheme$auth$host$port$path$query";
    }

    public function get_body_content() {
        return $this->body;
    }

    public function get_headers() {
        return $this->headers;
    }

    public function get_status_code() {
        return $this->status_code;
    }

    public function get_final_requested_uri() {
        return $this->url;
    }

    public function get_permanent_uri() {
        return $this->permanent_url;
    }
}

