namespace Deployer\Utility;

use Deployer\Exception\HttpieException;

class Httpie
{
    private string $method = 'GET';
    private string $url = '';
    private array $headers = [];
    private string $body = '';
    private array $curlopts = [];
    private bool $nothrow = false;

    public function __construct()
    {
        if (!extension_loaded('curl')) {
            throw new \Exception(
                "Please, install curl extension.\n" .
                "https://php.net/curl.installation",
            );
        }
    }

    public static function get(string $url): Httpie
    {
        $http = new self();
        $http->method = 'GET';
        $http->url = $url;
        return $http;
    }

    public static function post(string $url): Httpie
    {
        $http = new self();
        $http->method = 'POST';
        $http->url = $url;
        return $http;
    }

    public static function patch(string $url): Httpie
    {
        $http = new self();
        $http->method = 'PATCH';
        $http->url = $url;
        return $http;
    }

    public static function put(string $url): Httpie
    {
        $http = new self();
        $http->method = 'PUT';
        $http->url = $url;
        return $http;
    }

    public static function delete(string $url): Httpie
    {
        $http = new self();
        $http->method = 'DELETE';
        $http->url = $url;
        return $http;
    }

    public function query(array $params): self
    {
        $this->url .= '?' . http_build_query($params);
        return $this;
    }

    public function header(string $header, string $value): self
    {
        $this->headers[$header] = $value;
        return $this;
    }

    public function body(string $body): self
    {
        $this->body = $body;
        $this->headers = array_merge($this->headers, [
            'Content-Length' => strlen($this->body),
        ]);
        return $this;
    }

    public function jsonBody(array $data): self
    {
        $this->body = json_encode($data, JSON_PRETTY_PRINT);
        $this->headers = array_merge($this->headers, [
            'Content-Type' => 'application/json',
            'Content-Length' => strlen($this->body),
        ]);
        return $this;
    }

    public function formBody(array $data): self
    {
        $this->body = http_build_query($data);
        $this->headers = array_merge($this->headers, [
            'Content-type' => 'application/x-www-form-urlencoded',
            'Content-Length' => strlen($this->body),
        ]);
        return $this;
    }

    public function setopt(int $key, $value): self
    {
        $this->curlopts[$key] = $value;
        return $this;
    }

    public function nothrow(bool $on = true): self
    {
        $this->nothrow = $on;
        return $this;
    }

    public function send(?array &$info = null): string
    {
        if ($this->url === '') {
            throw new \RuntimeException('URL must not be empty to Httpie::send()');
        }
        $ch = curl_init($this->url);
        curl_setopt($ch, CURLOPT_USERAGENT, 'Deployer ' . DEPLOYER_VERSION);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $this->method);
        $headers = [];
        foreach ($this->headers as $key => $value) {
            $headers[] = "$key: $value";
        }
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $this->body);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_MAXREDIRS, 10);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 5);
        curl_setopt($ch, CURLOPT_TIMEOUT, 5);
        foreach ($this->curlopts as $key => $value) {
            curl_setopt($ch, $key, $value);
        }
        $result = curl_exec($ch);
        $info = curl_getinfo($ch);
        if ($result === false) {
            if ($this->nothrow) {
                $result = '';
            } else {
                $error = curl_error($ch);
                $errno = curl_errno($ch);
                curl_close($ch);
                throw new HttpieException($error, $errno);
            }
        }
        curl_close($ch);
        return $result;
    }

    public function getJson(): mixed
    {
        $result = $this->send();
        $response = json_decode($result, true);
        if (json_last_error() !== JSON_ERROR_NONE) {
            throw new HttpieException(
                'JSON Error: ' . json_last_error_msg() . '\n' .
                'Response: ' . $result,
            );
        }
        return $response;
    }
}

namespace Sentry\Spotlight;

use Sentry\HttpClient\Request;
use Sentry\HttpClient\Response;

class SpotlightClient
{
    public static function sendRequest(Request $request, string $url): Response
    {
        if (!\extension_loaded('curl')) {
            throw new \RuntimeException('The cURL PHP extension must be enabled to use the SpotlightClient.');
        }

        $requestData = $request->getStringBody();
        if ($requestData === null) {
            throw new \RuntimeException('The request data is empty.');
        }

        $curlHandle = curl_init();

        curl_setopt($curlHandle, \CURLOPT_URL, $url);
        curl_setopt($curlHandle, \CURLOPT_HTTPHEADER, [
            'Content-Type: application/x-sentry-envelope',
        ]);
        curl_setopt($curlHandle, \CURLOPT_TIMEOUT, 2.0);
        curl_setopt($curlHandle, \CURLOPT_CONNECTTIMEOUT, 1.0);
        curl_setopt($curlHandle, \CURLOPT_ENCODING, '');
        curl_setopt($curlHandle, \CURLOPT_POST, true);
        curl_setopt($curlHandle, \CURLOPT_POSTFIELDS, $requestData);
        curl_setopt($curlHandle, \CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curlHandle, \CURLOPT_HTTP_VERSION, \CURL_HTTP_VERSION_1_1);

        $body = curl_exec($curlHandle);

        if ($body === false) {
            $errorCode = curl_errno($curlHandle);
            $error = curl_error($curlHandle);
            curl_close($curlHandle);

            $message = 'cURL Error (' . $errorCode . ') ' . $error;

            return new Response(0, [], $message);
        }

        $statusCode = curl_getinfo($curlHandle, \CURLINFO_HTTP_CODE);

        curl_close($curlHandle);

        return new Response($statusCode, [], '');
    }
}

function request($url, $auth = true, $customRequest = null, $postFields = null)
{
    $user = Auth::user();
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_USERAGENT, 'Mozilla/5.0 (Windows NT 5.1; rv:31.0) Gecko/20100101 Firefox/31.0');
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_TIMEOUT, 30);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_AUTOREFERER, true);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);

    if (env('PROXY_PORT')) {
        curl_setopt($ch, CURLOPT_PROXYPORT, env('PROXY_PORT'));
        curl_setopt($ch, CURLOPT_PROXYTYPE, env('PROXY_METHOD'));
        curl_setopt($ch, CURLOPT_PROXY, env('PROXY_SERVER'));
    }

    if (env('PROXY_USER')) {
        curl_setopt($ch, CURLOPT_PROXYUSERPWD, env('PROXY_USER').':'.env('PROXY_PASS'));
    }

    if (!is_null($postFields)) {
        $postFields = json_encode($postFields);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $postFields);
        curl_setopt($ch, CURLOPT_HTTPHEADER, ['Content-Type: application/json',
            'Content-Length: '.strlen($postFields), ]);
    }

    if (strtolower($user->provider) == 'bitbucket') {
        curl_setopt($ch, CURLOPT_HTTPHEADER, ['Authorization: Bearer '.$user->token]);
    }

    if (!is_null($customRequest)) {
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $customRequest);
    }

    if ($auth && isset($user->username) && strtolower($user->provider) != 'bitbucket') {
        curl_setopt($ch, CURLOPT_HTTPAUTH, CURLAUTH_BASIC);
        curl_setopt($ch, CURLOPT_USERPWD, $user->username.':'.$user->token);
    }

    $result = curl_exec($ch);
    curl_close($ch);

    return json_decode($result);
}

