    public $proxy;
    public $curlInfo;

    public function __construct($OPENAI_API_KEY) {
        $this->headers = [
            'Authorization: Bearer ' . $OPENAI_API_KEY
        ];
    }

    public function sendRequest(string $url, string $method, array $opts = []) {
        $this->baseUrl($url);

        $ch = curl_init();

        $headers = $this->headers;
        if (isset($opts['headers'])) {
            $headers = array_merge($headers, (array)$opts['headers']);
        }

        $hasContentType = false;
        foreach ($headers as $header) {
            if (stripos(trim($header), 'Content-Type:') === 0) {
                $hasContentType = true;
                break;
            }
        }

        if (!$hasContentType) {
            $contentType = 'application/json';
            if (isset($opts['content_type'])) {
                $ct = $opts['content_type'];
                if (isset($this->contentTypes[$ct])) {
                    $contentType = $this->contentTypes[$ct];
                } else {
                    $contentType = $ct;
                }
            }
            $headers[] = 'Content-Type: ' . $contentType;
        }

        $options = [
            CURLOPT_URL => $url,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_CUSTOMREQUEST => $method,
            CURLOPT_HTTPHEADER => $headers,
            CURLOPT_TIMEOUT => (int)$this->timeout
        ];

        if ($this->proxy) {
            $options[CURLOPT_PROXY] = $this->proxy;
        }

        if (is_callable($this->stream_method)) {
            $options[CURLOPT_WRITEFUNCTION] = $this->stream_method;
            $options[CURLOPT_RETURNTRANSFER] = false;
        }

        if (in_array(strtoupper($method), ['POST', 'PUT', 'PATCH'])) {
            $options[CURLOPT_POST] = true;
            if (isset($opts['data'])) {
                $contentType = '';
                foreach ($headers as $header) {
                    if (stripos(trim($header), 'Content-Type:') === 0) {
                        $contentType = trim(preg_replace('/Content-Type:\s*/i', '', $header));
                        break;
                    }
                }

                if (stripos($contentType, 'application/json') !== false) {
                    $options[CURLOPT_POSTFIELDS] = json_encode($opts['data']);
                } elseif (stripos($contentType, 'multipart/form-data') !== false) {
                    $options[CURLOPT_POSTFIELDS] = http_build_query($opts['data']);
                } else {
                    $options[CURLOPT_POSTFIELDS] = $opts['data'];
                }
            }
        } elseif (strtoupper($method) === 'GET' && isset($opts['data'])) {
            $queryString = http_build_query($opts['data']);
            $url .= (parse_url($url, PHP_URL_QUERY) ? '&' : '?') . $queryString;
            $options[CURLOPT_URL] = $url;
        }

        curl_setopt_array($ch, $options);

        $response = curl_exec($ch);
        $this->curlInfo = curl_getinfo($ch);
        $error = curl_error($ch);
        curl_close($ch);

        if ($error) {
            return false;
        }

        return $response;
    }

    public function baseUrl(&$url) {
        if ($this->customUrl) {
            $defaultBase = 'https://api.openai.com/v1/';
            if (strpos($url, $defaultBase) === 0) {
                $url = $this->customUrl . substr($url, strlen($defaultBase));
            }
        }
    }
}

