        $token = getenv('BACKEND_TOKEN');
        return $this->setCurl($url, $token, 'add', $id);
    }

    public function restartTask(string $id): array {
        $url = getenv('BACKEND_URL');
        $token = getenv('BACKEND_TOKEN');
        return $this->setCurl($url, $token, 'restart', $id);
    }

    public function removeTask(string $id): array {
        $url = getenv('BACKEND_URL');
        $token = getenv('BACKEND_TOKEN');
        return $this->setCurl($url, $token, 'remove', $id);
    }

    private function setCurl(string $backendUrl, string $backendToken, string $operation, string $id): array {
        if (empty($backendUrl) || empty($backendToken)) {
            return ['status' => false, 'message' => 'Backend URL or token not configured.'];
        }

        $ch = curl_init();
        if (!$ch) {
            return ['status' => false, 'message' => 'Failed to initialize cURL.'];
        }

        $postData = json_encode([
            'id' => $id,
            'operation' => $operation,
        ]);

        curl_setopt_array($ch, [
            CURLOPT_URL            => $backendUrl,
            CURLOPT_POST           => true,
            CURLOPT_POSTFIELDS     => $postData,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_HTTPHEADER     => [
                'Content-Type: application/json',
                'Authorization: Bearer ' . $backendToken,
            ],
            CURLOPT_TIMEOUT        => 10,
        ]);

        $response = curl_exec($ch);
        $curlError = curl_error($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($curlError) {
            return ['status' => false, 'message' => 'cURL error: ' . $curlError];
        }

        if ($httpCode < 200 || $httpCode >= 300) {
            return ['status' => false, 'message' => "HTTP error {$httpCode}"];
        }

        $responseData = json_decode($response, true);
        if (json_last_error() !== JSON_ERROR_NONE) {
            return ['status' => false, 'message' => 'Invalid JSON response.'];
        }

        if (!isset($responseData['status'], $responseData['message'])) {
            return ['status' => false, 'message' => 'Missing required fields in response.'];
        }

        return [
            'status' => $responseData['status'],
            'message' => $responseData['message'],
        ];
    }
}

