function file_get_contents_by_socket($url, &$data)
{
    $timeout = 1;
    $p = parse_url($url);
    $file = $p['path'];
    if ($p['scheme'] == 'https') {
        $prefix = 'ssl://';
        $port = ($p['port'] ? $p['port'] : 443);
    } else {
        $prefix = '';
        $port = ($p['port'] ? $p['port'] : 80);
    }
    if ($p['query']) {
        $file .= '?' . $p['query'];
    }
    if (!($fh = @fsockopen($prefix . $p['host'], $port, $errno, $errstr, $timeout))) {
        return false;
    }

    $getstring =
        "GET " . $file . " HTTP/1.0 \r\n" .
        "Host: " . $p['host'] . " \r\n" .
        "Connection: close\r\n\r\n";
    fwrite($fh, $getstring);
    $s = fgets($fh, 1024);
    if (!$s) {
        return false;
    }
    $httpheader .= $s;
    while (!feof($fh)) {
        $s = fgets($fh, 1024);
        if ($s == "\r\n") {
            break;
        }
    }
    $data = '';
    while (!feof($fh)) {
        $data .= fgets($fh, 1024);
    }
    fclose($fh);
}

