class Radius
{
    var $_ip_radius_server;
    var $_shared_secret;
    var $_radius_suffix;
    var $_udp_timeout;
    var $_authentication_port;
    var $_accounting_port;
    var $_nas_ip_address;
    var $_nas_port;
