    var $_response_authenticator;
    var $_username;
    var $_password;
    var $_identifier_to_send;
    var $_identifier_received;
    var $_radius_packet_to_send;
    var $_radius_packet_received;
    var $_attributes_to_send;
    var $_attributes_received;
    var $_socket_to_server;
    var $_debug_mode;
    var $debug_text = [];
    var $_attributes_info;
    var $_radius_packet_info;
    var $_last_error_code;
    var $_last_error_message;

    public function __construct($ip_radius_server = '127.0.0.1', $shared_secret = '', $radius_suffix = '', $udp_timeout = 5, $authentication_port = 1812, $accounting_port = 1813)
    {
        $this->_radius_packet_info[1] = 'Access-Request';
        $this->_radius_packet_info[2] = 'Access-Accept';
        $this->_radius_packet_info[3] = 'Access-Reject';
        $this->_radius_packet_info[4] = 'Accounting-Request';
        $this->_radius_packet_info[5] = 'Accounting-Response';
        $this->_radius_packet_info[11] = 'Access-Challenge';
        $this->_radius_packet_info[12] = 'Status-Server (experimental)';
        $this->_radius_packet_info[13] = 'Status-Client (experimental)';
        $this->_radius_packet_info[255] = 'Reserved';

        $this->_identifier_to_send = 0;
        $this->_user_ip_address = (isset($_SERVER['REMOTE_ADDR']) ? $_SERVER['REMOTE_ADDR'] : '0.0.0.0');

        $this->GenerateRequestAuthenticator();
        $this->SetIpRadiusServer($ip_radius_server);
        $this->SetSharedSecret($shared_secret);
        $this->SetAuthenticationPort($authentication_port);
        $this->SetAccountingPort($accounting_port);
        $this->SetRadiusSuffix($radius_suffix);
        $this->SetUdpTimeout($udp_timeout);
        $this->SetUsername();
        $this->SetPassword();
        $this->SetNasIpAddress();
        $this->SetNasPort();

        $this->ClearLastError();
        $this->ClearDataToSend();
        $this->ClearDataReceived();
    }

    function AccessRequest($username = '', $password = '', $udp_timeout = 0, $state = NULL)
    {
        $this->ClearDataReceived();
        $this->ClearLastError();

        $this->SetPacketCodeToSend(1);

        if (0 < strlen($username))
        {
            $this->SetUsername($username);
        }

        if (0 < strlen($password))
        {
            $this->SetPassword($password);
        }

        if ($state !== NULL)
        {
            $this->SetAttribute(24, $state);
        }
        else
        {
            $this->SetAttribute(6, 1);
        }

        if (intval($udp_timeout) > 0)
        {
            $this->SetUdpTimeout($udp_timeout);
        }

        $attributes_content = '';
        $attribute_count1 = count((array)$this->_attributes_to_send);
        for ($attributes_loop = 0; $attributes_loop < $attribute_count1; $attributes_loop++)
        {
            $attributes_content .= $this->_attributes_to_send[$attributes_loop];
        }

        $packet_length  = 4;
        $packet_length += strlen($this->_request_authenticator);
        $packet_length += strlen($attributes_content);

        $packet_data  = chr($this->_radius_packet_to_send);
        $packet_data .= chr($this->GetNextIdentifier());
        $packet_data .= chr(intval($packet_length / 256));
        $packet_data .= chr(intval($packet_length % 256));
        $packet_data .= $this->_request_authenticator;
        $packet_data .= $attributes_content;

        $_socket_to_server = socket_create(AF_INET, SOCK_DGRAM, 17);

        if ($_socket_to_server === FALSE)
        {
            $this->_last_error_code    = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
        }
        elseif (FALSE === socket_connect($_socket_to_server, $this->_ip_radius_server, $this->_authentication_port))
        {
            $this->_last_error_code    = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
        }
        elseif (FALSE === socket_write($_socket_to_server, $packet_data, $packet_length))
        {
            $this->_last_error_code    = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
        }
        else
        {
            $read_socket_array   = array($_socket_to_server);
            $write_socket_array  = NULL;
            $except_socket_array = NULL;

            $received_packet = chr(0);

            if (!(FALSE === socket_select($read_socket_array, $write_socket_array, $except_socket_array, $this->_udp_timeout)))
            {
                if (in_array($_socket_to_server, $read_socket_array))
                {
                    if (FALSE === ($received_packet = @socket_read($_socket_to_server, 1024)))
                    {
                        $received_packet = chr(0);
                        $this->_last_error_code    = socket_last_error();
                        $this->_last_error_message = socket_strerror($this->_last_error_code);
                    }
                    else
                    {
                        socket_close($_socket_to_server);
                    }
                }
            }
            else
            {
                socket_close($_socket_to_server);
            }
        }

        $this->_radius_packet_received = intval(ord(substr($received_packet, 0, 1)));

        if ($this->_radius_packet_received > 0)
        {
            $this->_identifier_received = intval(ord(substr($received_packet, 1, 1)));
            $packet_length = (intval(ord(substr($received_packet, 2, 1))) * 256) + (intval(ord(substr($received_packet, 3, 1)));
            $this->_response_authenticator = substr($received_packet, 4, 16);
            $attributes_content = substr($received_packet, 20, ($packet_length - 4 - 16));
            while (strlen($attributes_content) > 2)
            {
                $attribute_type = intval(ord(substr($attributes_content, 0, 1)));
                $attribute_length = intval(ord(substr($attributes_content, 1, 1)));
                $attribute_raw_value = substr($attributes_content, 2, $attribute_length - 2);
                $attributes_content = substr($attributes_content, $attribute_length);

                $attribute_value = $this->DecodeAttribute($attribute_raw_value, $attribute_type);

                $this->_attributes_received[] = array($attribute_type, $attribute_value);
            }
        }

        return (2 == ($this->_radius_packet_received));
    }
}

namespace common\modules\torrent_scraper\models;

use common\models\ScraperException;

class UdpScraper extends BaseScraper
{
    const ACTION_CONNECT = 0;
    const ACTION_ANNOUNCE = 1;
    const ACTION_SCRAPE = 2;
    const ACTION_ERROR = 3;

    const CONNECTION_ID = "\x00\x00\x04\x17\x27\x10\x19\x80";

    protected $currentConnectionId = null;
    protected $transactionId = null;
    protected $socket = null;
    protected $timeout = 15;

    public function scrape(Tracker $tracker, array $hashes)
    {
        $chunks = array_chunk($hashes, 74);
        $result = [];
        foreach ($chunks as $chunk) {
            $result = array_merge($result, $this->scrapeChunk($tracker, $chunk));
        }
        return $result;
    }

    public function scrapeChunk(Tracker $tracker, $hashes)
    {
        $this->initSocket();
        $this->connect($tracker->host, $tracker->port);
        $this->startTransaction();
        $torrents = $this->scrapeHashes($hashes);
        $this->closeSocket();
        return $torrents;
    }

    protected function initSocket()
    {
        if ($this->socket === null) {
            $socket = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);
            if ($socket === false) {
                throw new UdpScraperException(socket_strerror(socket_last_error()));
            }
            $this->socket = $socket;
        }
        return $this->socket;
    }

    protected function connect($domain, $port)
    {
        if (socket_connect($this->socket, $domain, $port) === false) {
            throw new UdpScraperException(socket_strerror(socket_last_error($this->socket)));
        }
    }

    protected function generateTransactionId()
    {
        $this->transactionId = mt_rand(0, 65535);
    }

    protected function startTransaction()
    {
        $this->generateTransactionId();
        $packet = static::CONNECTION_ID . pack('N', static::ACTION_CONNECT) . pack('N', $this->transactionId);
        $res = $this->request($packet, 16);
        if (strlen($res) < 1) {
            throw new UdpScraperException('No connection response.');
        }
        if (strlen($res) < 16) {
            throw new UdpScraperException('Too short connection response.');
        }
        $resUnpacked = unpack("Naction/Ntransid", $res);

        if ($resUnpacked['action'] != static::ACTION_CONNECT || $resUnpacked['transid'] != $this->transactionId) {
            throw new UdpScraperException('Invalid connection response.');
        }

        $this->currentConnectionId = substr($res, 8, 8);
    }

    protected function scrapeHashes($hashes)
    {
        $hashString = '';
        foreach ($hashes as $hash) {
            $hashString .= pack('H*', $hash);
        }

        $packet = $this->currentConnectionId . pack("N", static::ACTION_SCRAPE) . pack("N", $this->transactionId) . $hashString;

        $readLength = 8 + (12 * count($hashes));

        $res = $this->request($packet, $readLength);

        if (strlen($res) < 1) {
            throw new UdpScraperException('No scrape response.');
        }
        if (strlen($res) < 8) {
            throw new UdpScraperException('Too short scrape response.');
        }

        $resUnpacked = unpack("Naction/Ntransid", $res);

        if ($resUnpacked['action'] == static::ACTION_ERROR) {
            $this->checkError($res);
        }

        if (strlen($res) < $readLength) {
            throw new UdpScraperException('Too short scrape response.');
        }

        $torrents = [];
        $index = 8;
        foreach ($hashes as $hash) {
            $resUnpacked = unpack("Ncomplete/Ndownloaded/Nincomplete", substr($res, $index, 12));
            $torrents[$hash] = $resUnpacked;
            $index = $index + 12;
        }
        return $torrents;
    }

    protected function request($packet, $readLength)
    {
        socket_set_option($this->socket, SOL_SOCKET, SO_RCVTIMEO, ['sec' => $this->timeout, 'usec' => 0]);
        socket_set_option($this->socket, SOL_SOCKET, SO_SNDTIMEO, ['sec' => $this->timeout, 'usec' => 0]);

        if (socket_write($this->socket, $packet, strlen($packet)) === false) {
            throw new UdpScraperException(socket_strerror(socket_last_error($this->socket)));
        }

        $res = socket_read($this->socket, $readLength, PHP_BINARY_READ);
        return $res;
    }

    protected function closeSocket()
    {
        if ($this->socket) {
            socket_close($this->socket);
            $this->socket = null;
            $this->transactionId = null;
            $this->currentConnectionId = null;
        }
    }

    protected function checkError($response)
    {
        $unpacked = unpack("Naction/Ntransid/H*error", $response);
        throw new UdpScraperException($unpacked['error']);
    }
}

use Swoole\Coroutine\Client;
use SMProxy\MysqlPacket\AuthPacket;
use SMProxy\MysqlPacket\BinaryPacket;
use SMProxy\MysqlPacket\ErrorPacket;
use SMProxy\MysqlPacket\HandshakePacket;
use SMProxy\MysqlPacket\MySQLMessage;
use SMProxy\MysqlPacket\OkPacket;
use SMProxy\MysqlPacket\Util\Capabilities;
use SMProxy\MysqlPacket\Util\CharsetUtil;
use SMProxy\MysqlPacket\Util\SecurityUtil;
use SMProxy\MysqlPool\MySQLException;
use SMProxy\MysqlPool\MySQLPool;

class MysqlProxy
{
    private $isDuplex;
    public $server;
    public $serverFd;
    public $charset;
    public $account;
    public $auth = false;
    public $chan;
    public $serverPublicKey;
    public $salt;
    public $connected = false;
    public $timeout = 0.1;
    public $mysqlClient;
    public $mysqlServer;
    public $client;

    public function __construct(\swoole_server $server, int $fd, \Swoole\Coroutine\Channel $chan)
    {
        $this->server = $server;
        $this->serverFd = $fd;
        $this->chan = $chan;
        $this->client = new Client(CONFIG['server']['swoole_client_sock_setting']['sock_type'] ?? SWOOLE_SOCK_TCP);
        $this->client->set(CONFIG['server']['swoole_client_setting'] ?? []);
        $this->client->set(packageLengthSetting());
        $this->isDuplex = version_compare(SWOOLE_VERSION, '4.2.13', '>=');
        if (!$this->isDuplex) {
            $this->mysqlClient = new Channel(1);
        }
    }

    public function connect(string $host, int $port, float $timeout = 0.1, int $tryStep = 0)
    {
        $this->timeout = $timeout;
        if (!$this->client->connect($host, $port, $timeout)) {
            if ($tryStep < 3) {
                $this->client->close();
                return $this->connect($host, $port, $timeout, ++$tryStep);
            } else {
                $this->onClientError($this->client);
                return false;
            }
        } else {
            if (!$this->isDuplex) {
                $this->mysqlClient->push($this->client);
            }
            self::go(function () {
                $remain = '';
                while (true) {
                    $data = $this->recv($remain);
                    if ($data === '' || $data === false) {
                        break;
                    }
                }
            });
            return $this->client;
        }
    }

    public function onClientReceive(\Swoole\Coroutine\Client $cli, string $data)
    {
        self::go(function () use ($cli, $data) {
            $fd = $this->serverFd;
            $binaryPacket = new BinaryPacket();
            $binaryPacket->data = getBytes($data);
            $binaryPacket->packetLength = $binaryPacket->calcPacketSize();
            if (isset($binaryPacket->data[4])) {
                $send = true;
                if ($binaryPacket->data[4] == ErrorPacket::$FIELD_COUNT) {
                    $errorPacket = new ErrorPacket();
                    $errorPacket->read($binaryPacket);
                    $data = getString($errorPacket->write());
                } elseif (!$this->connected) {
                    if ($binaryPacket->data[4] == OkPacket::$FIELD_COUNT) {
                        $send = false;
                        $this->connected = true;
                        $this->chan->push($this);
                    } elseif ($binaryPacket->data[4] == 0x01) {
                        if ($binaryPacket->packetLength == 6) {
                            if ($binaryPacket->data[$binaryPacket->packetLength - 1] == 4) {
                                $data = getString(array_merge(getMysqlPackSize(1), [3, 2]));
                                $this->send($data);
                            }
                        } else {
                            $this->serverPublicKey = substr($data, 5, strlen($data) - 2);
                            $encryptData = SecurityUtil::sha2RsaEncrypt($this->account['password'], $this->salt, $this->serverPublicKey);
                            $data = getString(array_merge(getMysqlPackSize(strlen($encryptData)), [5])) . $encryptData;
                            $this->send($data);
                        }
                        $send = false;
                    } elseif ($binaryPacket->data[4] == 0xfe) {
                        $mm = new MySQLMessage($binaryPacket->data);
                        $mm->move(5);
                        $pluginName = $mm->readStringWithNull();
                        $this->salt = $mm->readBytesWithNull();
                        $password = $this->processAuth($pluginName ?: 'mysql_native_password');
                        $this->send(getString(array_merge(getMysqlPackSize(count($password)), [3], $password)));
                        $send = false;
                    } elseif (!$this->auth) {
                        $handshakePacket = (new HandshakePacket())->read($binaryPacket);
                        $this->mysqlServer = $handshakePacket;
                        $this->salt = array_merge($handshakePacket->seed, $handshakePacket->restOfScrambleBuff);
                        $password = $this->processAuth($handshakePacket->pluginName);
                        $clientFlag = Capabilities::CLIENT_CAPABILITIES;
                        $authPacket = new AuthPacket();
                        $authPacket->pluginName = $handshakePacket->pluginName;
                        $authPacket->packetId = 1;
                        if (isset($this->database) && $this->database) {
                            $authPacket->database = $this->database;
                        } else {
                            $authPacket->database = 0;
                        }
                        if ($authPacket->database) {
                            $clientFlag |= Capabilities::CLIENT_CONNECT_WITH_DB;
                        }
                        if (version_compare($handshakePacket->serverVersion, '5.0', '>=')) {
                            $clientFlag |= Capabilities::CLIENT_MULTI_RESULTS;
                        }
                        $authPacket->clientFlags = $clientFlag;
                        $authPacket->serverCapabilities = $handshakePacket->serverCapabilities;
                        $authPacket->maxPacketSize =
                            CONFIG['server']['swoole_client_setting']['package_max_length'] ?? 16777215;
                        $authPacket->charsetIndex = CharsetUtil::getIndex($this->charset ?? 'utf8mb4');
                        $authPacket->user = $this->account['user'];
                        $authPacket->password = $password;
                        $this->auth = true;
                        $this->send(getString($authPacket->write()));
                        $send = false;
                    }
                }
                if ($send && $this->server->exist($fd)) {
                    $this->server->send($fd, $data);
                }
            }
        });
    }

    public function processAuth(string $pluginName)
    {
        switch ($pluginName) {
            case 'mysql_native_password':
                $password = SecurityUtil::scramble411($this->account['password'], $this->salt);
                break;
            case 'caching_sha2_password':
                $password = SecurityUtil::scrambleSha256($this->account['password'], $this->salt);
                break;
            case 'sha256_password':
                throw new MySQLException('Sha256_password plugin is not supported yet');
                break;
            case 'mysql_old_password':
                throw new MySQLException('mysql_old_password plugin is not supported yet');
                break;
            case 'mysql_clear_password':
                $password = array_merge(getBytes($this->account['password']), [0]);
                break;
            default:
                $password = SecurityUtil::scramble411($this->account['password'], $this->salt);
                break;
        }
        return $password;
    }

    public function send(...$data)
    {
        if ($this->isDuplex) {
            if ($this->client->isConnected()) {
                return $this->client->send(...$data);
            } else {
                return false;
            }
        } else {
            $client = self::coPop($this->mysqlClient);
            if ($client === false) {
                return false;
            }
            if ($client->isConnected()) {
                $result = $client->send(...$data);
                $this->mysqlClient->push($client);
                return $result;
            }
            return false;
        }
    }

    public function recv(&$remain)
    {
        if ($this->isDuplex) {
            $client = $this->client;
            $data = $client->recv(-1);
        } else {
            $client = self::coPop($this->mysqlClient, $this->timeout);
            if ($client === false) {
                return false;
            }
            if ($client->isConnected()) {
                $data = $client->recv($this->timeout / 500);
            } else {
                $data = '';
            }
            $this->mysqlClient->push($client);
            if ($data === false && $client->errCode == 110) {
                $data = true;
            }
            if ($data === '' || $data === false) {
                $this->mysqlClient->close();
            }
        }
        if ($data === '' || $data === false) {
            $this->onClientClose($client);
        } elseif (is_string($data)) {
            $this->onClientReceive($client, $data);
        }
        return $data;
    }

    public function onClientClose(\Swoole\Coroutine\Client $cli)
    {
        MySQLPool::destruct($cli, $this->connName);
    }

    public function onClientError(\Swoole\Coroutine\Client $cli)
    {
    }
}

use Mix\Bean\BeanInjector;
use Mix\JsonRpc\Constants;
use Mix\JsonRpc\Exception\DeserializeException;
use Mix\JsonRpc\Helper\JsonRpcHelper;
use Mix\JsonRpc\Middleware\MiddlewareDispatcher;
use Mix\JsonRpc\Middleware\MiddlewareInterface;
use Mix\JsonRpc\Message\Request;
use Mix\JsonRpc\Message\Response;
use Swoole\Coroutine\Client;

class Connection
{
    public $host = '';
    public $port = 0;
    public $timeout = 0.0;
    public $callTimeout = 10.0;
    public $middleware = [];
    protected $client;

    public function __construct(array $config = [])
    {
        BeanInjector::inject($this, $config);
    }

    public function connect()
    {
        $host    = $this->host;
        $port    = $this->port;
        $timeout = $this->timeout;
        $client  = new Client(SWOOLE_SOCK_TCP);
        $client->set([
            'open_eof_check' => true,
            'package_eof'    => Constants::EOF,
        ]);
        if (!$client->connect($host, $port, $timeout)) {
            throw new \Swoole\Exception(sprintf("JSON-RPC: %s (host:%s, port: %s)", $client->errMsg, $host, $port), $client->errCode);
        }
        $this->client = $client;
    }

    public function call(Request $request)
    {
        $process              = function (Request $request) {
            $jsonString = JsonRpcHelper::encode($request) . Constants::EOF;
            $this->send($jsonString);
            $data = $this->recv($this->callTimeout);
            return JsonRpcHelper::deserializeResponse($data);
        };
        $middlewareDispatcher = new MiddlewareDispatcher($this->middleware, $process, $request);
        return $middlewareDispatcher->dispatch();
    }

    public function close()
    {
        if (!$this->client->close()) {
            $errMsg  = $this->client->errMsg;
            $errCode = $this->client->errCode;
            if ($errMsg == '' && $errCode == 0) {
                return;
            }
            throw new \Swoole\Exception($errMsg, $errCode);
        }
    }

    protected function recv(float $timeout = -1)
    {
        $data = $this->client->recv($timeout);
        if ($data === false) {
            $client = $this->client;
            throw new \Swoole\Exception($client->errMsg, $client->errCode);
        }
        if ($data === "") {
            $errCode = stripos(PHP_OS, 'Darwin') !== false ? 54 : 104;
            $errMsg  = swoole_strerror($errCode, 9);
            throw new \Swoole\Exception($errMsg, $errCode);
        }
        return $data;
    }

    protected function send(string $data)
    {
        $len  = strlen($data);
        $size = $this->client->send($data);
        if ($size === false) {
            throw new \Swoole\Exception($this->client->errMsg, $this->client->errCode);
        }
        if ($len !== $size) {
            throw new \Swoole\Exception('The sending data is incomplete, it may be that the socket has been closed by the peer.');
        }
    }
}

namespace Symfony\Component\Mailer\Transport\Smtp\Stream;

use Symfony\Component\Mailer\Exception\TransportException;

final class ProcessStream extends AbstractStream
{
    private string $command;

    public function setCommand(string $command)
    {
        $this->command = $command;
    }

    public function initialize(): void
    {
        $descriptorSpec = [
            0 => ['pipe', 'r'],
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w'],
        ];
        $pipes = [];
        $this->stream = proc_open($this->command, $descriptorSpec, $pipes);
        stream_set_blocking($pipes[2], false);
        if ($err = stream_get_contents($pipes[2])) {
            throw new TransportException('Process could not be started: '.$err);
        }
        $this->in = &$pipes[0];
        $this->out = &$pipes[1];
    }

    public function terminate(): void
    {
        if (null !== $this->stream) {
            fclose($this->in);
            fclose($this->out);
            proc_close($this->stream);
        }

        parent::terminate();
    }

    protected function getReadConnectionDescription(): string
    {
        return 'process '.$this->command;
    }
}

use Monolog\Level;
use Monolog\LogRecord;

class ProcessHandler extends AbstractProcessingHandler
{
    private $process;
    private string $command;
    private ?string $cwd;
    private array $pipes = [];
    protected const DESCRIPTOR_SPEC = [
        0 => ['pipe', 'r'],
        1 => ['pipe', 'w'],
        2 => ['pipe', 'w'],
    ];

    public function __construct(string $command, int|string|Level $level = Level::Debug, bool $bubble = true, ?string $cwd = null)
    {
        if ($command === '') {
            throw new \InvalidArgumentException('The command argument must be a non-empty string.');
        }
        if ($cwd === '') {
            throw new \InvalidArgumentException('The optional CWD argument must be a non-empty string or null.');
        }

        parent::__construct($level, $bubble);

        $this->command = $command;
        $this->cwd = $cwd;
    }

    protected function write(LogRecord $record): void
    {
        $this->ensureProcessIsStarted();
        $this->writeProcessInput($record->formatted);

        $errors = $this->readProcessErrors();
        if ($errors !== '') {
            throw new \UnexpectedValueException(sprintf('Errors while writing to process: %s', $errors));
        }
    }

    private function ensureProcessIsStarted(): void
    {
        if (is_resource($this->process) === false) {
            $this->startProcess();
            $this->handleStartupErrors();
        }
    }

    private function startProcess(): void
    {
        $this->process = proc_open($this->command, static::DESCRIPTOR_SPEC, $this->pipes, $this->cwd);
        foreach ($this->pipes as $pipe) {
            stream_set_blocking($pipe, false);
        }
    }

    private function handleStartupErrors(): void
    {
        $selected = $this->selectErrorStream();
        if (false === $selected) {
            throw new \UnexpectedValueException('Something went wrong while selecting a stream.');
        }

        $errors = $this->readProcessErrors();
        if (is_resource($this->process) === false || $errors !== '') {
            throw new \UnexpectedValueException(
                sprintf('The process "%s" could not be opened: ' . $errors, $this->command)
            );
        }
    }

    protected function selectErrorStream()
    {
        $empty = [];
        $errorPipes = [$this->pipes[2]];
        return stream_select($errorPipes, $empty, $empty, 1);
    }

    protected function readProcessErrors(): string
    {
        return (string) stream_get_contents($this->pipes[2]);
    }

    protected function writeProcessInput(string $string): void
    {
        fwrite($this->pipes[0], $string);
    }

    public function close(): void
    {
        if (is_resource($this->process)) {
            foreach ($this->pipes as $pipe) {
                fclose($pipe);
            }
            proc_close($this->process);
            $this->process = null;
        }
    }
}

