class Picture {
    private $compressLimit;
    private $sizeLimit;

    public function __construct($compressLimit, $sizeLimit) {
        $this->compressLimit = $compressLimit;
        $this->sizeLimit = $sizeLimit;
    }

    public function downloadImage($url) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        $imageData = curl_exec($ch);
        curl_close($ch);

        if ($imageData === false) {
            return false;
        }

        $imageInfo = getimagesizefromstring($imageData);
        if ($imageInfo === false) {
            return false;
        }

        list($width, $height) = $imageInfo;
        $imageType = $imageInfo[2];

        $image = null;
        switch ($imageType) {
            case IMAGETYPE_JPEG:
                $image = imagecreatefromjpeg($imageData);
                break;
            case IMAGETYPE_PNG:
                $image = imagecreatefrompng($imageData);
                break;
            case IMAGETYPE_GIF:
                $image = imagecreatefromgif($imageData);
                break;
            default:
                return false;
        }

        if (!$image) {
            return false;
        }

        $targetWidth = $width;
        $targetHeight = $height;
        if ($width > $this->sizeLimit || $height > $this->sizeLimit) {
            $ratio = $width / $height;
            if ($width > $height) {
                $targetWidth = $this->sizeLimit;
                $targetHeight = $targetWidth / $ratio;
            } else {
                $targetHeight = $this->sizeLimit;
                $targetWidth = $targetHeight * $ratio;
            }

            $thumb = imagecreatetruecolor($targetWidth, $targetHeight);
            imagecopyresampled($thumb, $image, 0, 0, 0, 0, $targetWidth, $targetHeight, $width, $height);
            imagedestroy($image);
            $image = $thumb;
        }

        ob_start();
        switch ($imageType) {
            case IMAGETYPE_JPEG:
                imagejpeg($image, null, 85);
                break;
            case IMAGETYPE_PNG:
                imagepng($image, null, 9);
                break;
            case IMAGETYPE_GIF:
                imagegif($image);
                break;
        }
        $resizedData = ob_get_clean();

        $size = strlen($resizedData);
        if ($size > $this->compressLimit) {
            ob_start();
            switch ($imageType) {
                case IMAGETYPE_JPEG:
                    imagejpeg($image, null, 75);
                    break;
                case IMAGETYPE_PNG:
                    imagepng($image, null, 9);
                    break;
                case IMAGETYPE_GIF:
                    imagegif($image);
                    break;
            }
            $compressedData = ob_get_clean();
            $imageData = $compressedData;
        } else {
            $imageData = $resizedData;
        }

        imagedestroy($image);
        return $imageData;
    }
}

