function fetchContent($params, $template) {
    if (!isset($params['file'])) return;
    $file = $params['file'];
    $url = parse_url($file);
    $content = '';
    if (isset($url['scheme']) && in_array($url['scheme'], ['http', 'https'])) {
        $proxy_host = $params['proxy_host'] ?? null;
        $proxy_port = $params['proxy_port'] ?? null;
        $host = $proxy_host ?: $url['host'];
        $port = $proxy_port ?: ($url['port'] ?? ($url['scheme'] === 'https' ? 443 : 80));
        $context = null;
        if ($url['scheme'] === 'https') {
            $context = stream_context_create(['ssl' => ['verify_peer' => false, 'verify_peer_name' => false]]);
        }
        $fp = fsockopen(($url['scheme'] === 'https' ? 'ssl://' : '') . $host, $port, $errno, $errstr, 30);
        if (!$fp) return;
        $request = $proxy_host ? "GET $file HTTP/1.1\r\n" : "GET " . ($url['path'] ?: '/') . (isset($url['query']) ? '?' . $url['query'] : '') . " HTTP/1.1\r\n";
        $request .= "Host: " . $url['host'] . "\r\n";
        if (isset($params['user']) && isset($params['pass'])) {
            $auth = base64_encode($params['user'] . ':' . $params['pass']);
            $request .= "Authorization: Basic $auth\r\n";
        }
        $request .= "Connection: Close\r\n\r\n";
        fwrite($fp, $request);
        $response = '';
        while (!feof($fp)) $response .= fgets($fp, 1024);
        fclose($fp);
        list($headers, $body) = explode("\r\n\r\n", $response, 2);
        $status = substr($headers, 0, strpos($headers, "\r\n"));
        if (strpos($status, '200') === false) return;
        if (isset($params['assign_headers'])) $template->assign($params['assign_headers'], $headers);
        $content = $body;
    } else {
        $filePath = $file;
        if (!file_exists($filePath)) return;
        $allowedDir = '/var/www/allowed/';
        $realPath = realpath($filePath);
        if (strpos($realPath, $allowedDir) !== 0) return;
        $content = file_get_contents($filePath);
    }
    if (isset($url['scheme']) && !$template->isTrusted($file)) return;
    if (isset($params['assign'])) $template->assign($params['assign'], $content);
    else return $content;
}
