class CI_Trackback {
    protected $charset = 'UTF-8';
    protected $data = [];
    protected $convert_ascii = true;
    protected $response = '';
    protected $error_msg = [];

    public function process($url, $data) {
        $parsed = parse_url($url);
        if (!isset($parsed['host'])) {
            $this->set_error("Invalid URL: $url");
            return false;
        }

        $host = $parsed['host'];
        $port = isset($parsed['port']) ? $parsed['port'] : 80;
        $path = isset($parsed['path']) ? $parsed['path'] : '/';

        $fp = fsockopen($host, $port, $errno, $errstr, 30);
        if (!$fp) {
            $this->set_error("Socket error: $errstr ($errno)");
            return false;
        }

        $request = "POST $path HTTP/1.1\r\n";
        $request .= "Host: $host\r\n";
        $request .= "Content-Type: application/x-www-form-urlencoded\r\n";
        $request .= "Content-Length: " . strlen($data) . "\r\n";
        $request .= "Connection: Close\r\n\r\n";
        $request .= $data;

        fwrite($fp, $request);

        $response = '';
        while (!feof($fp)) {
            $response .= fgets($fp, 1024);
        }

        fclose($fp);

        $this->response = $response;

        $lines = explode("\r\n", $response);
        $status_line = array_shift($lines);
        preg_match('/^HTTP\/\S+ (\d+)/', $status_line, $matches);
        $status_code = $matches[1] ?? 0;

        if ($status_code >= 200 && $status_code < 300) {
            return true;
        } else {
            $this->set_error("Trackback failed with status code: $status_code");
            return false;
        }
    }

    public function get_id($url) {
        if (preg_match('/(\d+)/', $url, $matches)) {
            return $matches[1];
        }
        return false;
    }

    public function set_error($msg) {
        $this->error_msg[] = $msg;
    }
}

