class Http {
    public static function fsockopenDownload($url, $conf = []) {
        $defaults = [
            'limit' => 0,
            'post' => null,
            'cookie' => '',
            'ip' => null,
            'timeout' => 10,
            'block' => true,
        ];
        $conf = array_merge($defaults, $conf);

        $parsed = parse_url($url);
        $scheme = $parsed['scheme'] ?? 'http';
        $host = $conf['ip'] ?? ($parsed['host'] ?? '');
        $port = $parsed['port'] ?? ($scheme === 'https' ? 443 : 80);
        $path = $parsed['path'] ?? '/';
        $query = $parsed['query'] ?? '';
        $fragment = $parsed['fragment'] ?? '';

        $method = $conf['post'] ? 'POST' : 'GET';

        $defaultPort = ($scheme === 'https') ? 443 : 80;
        $hostWithPort = ($port === $defaultPort) ? $host : "$host:$port";

        $requestLine = "$method $path" . ($query ? "?$query" : '') . " HTTP/1.1\r\n";

        $headers = [];
        $headers[] = "Host: $hostWithPort";
        if ($conf['cookie']) {
            $headers[] = "Cookie: {$conf['cookie']}";
        }

        $requestBody = '';
        if ($method === 'POST') {
            $postData = $conf['post'];
            if (is_array($postData)) {
                $postData = http_build_query($postData);
            }
            $headers[] = "Content-Type: application/x-www-form-urlencoded";
            $headers[] = "Content-Length: " . strlen($postData);
            $requestBody = $postData;
        }

        $headersStr = implode("\r\n", $headers) . "\r\n";
        $request = $requestLine . $headersStr . $requestBody;

        $blocking = $conf['block'] ? 1 : 0;
        $hostToUse = $scheme === 'https' ? 'ssl://' . $host : $host;
        $errno = 0;
