    protected $_redis;
    protected $_key_prefix;
    protected $_lock_key;
    protected $_key_exists;
    protected $_config;
    protected $_session_id;
    protected $_fingerprint;

    public function __construct(&$params) {
        $this->_config = $params;
        $this->_redis = new Redis();
        $this->_redis->connect($this->_config['host'], $this->_config['port']);
        if (isset($this->_config['password'])) {
            $this->_redis->auth($this->_config['password']);
        }
        $this->_key_prefix = $this->_config['prefix'] . '_';
    }

    public function open($save_path, $name) {
        return true;
    }

    public function read($session_id) {
        $this->_session_id = $session_id;
        $key = $this->_key_prefix . $session_id;
        $data = $this->_redis->get($key);
        $this->_key_exists = (bool)$data;
        $this->_fingerprint = md5($data);
        return $data;
    }

    public function write($session_id, $session_data) {
        $this->_session_id = $session_id;
        $key = $this->_key_prefix . $session_id;
        $ttl = $this->_config['ttl'] ?? 3600;
        return $this->_redis->setex($key, $ttl, $session_data);
    }

    public function close() {
        $this->_release_lock();
        return true;
    }

    public function destroy($session_id) {
        $key = $this->_key_prefix . $session_id;
        $this->_redis->del($key);
        return true;
    }

    public function gc($maxlifetime) {
        return true;
    }

    protected function _get_lock($session_id) {
        $this->_lock_key = $this->_key_prefix . 'lock:' . $session_id;
        return $this->_redis->setnx($this->_lock_key, 1);
    }

    protected function _release_lock() {
        if ($this->_lock_key) {
            $this->_redis->del($this->_lock_key);
            $this->_lock_key = null;
        }
        return true;
    }

    protected function _fail() {
        return false;
    }

    protected function _success() {
        return true;
    }
}

