class CI_Cache_redis extends CI_Driver {
    protected $_default_config = [
        'host' => '127.0.0.1',
        'port' => 6379,
        'timeout' => 0.0,
        'persistent' => false,
        'username' => null,
        'password' => null,
        'database' => 0,
    ];

    protected $_redis;
    protected $_serialized = [];

    public function __construct($params) {
        if (!$this->is_supported()) {
            return false;
        }

        $config = array_merge($this->_default_config, $params);
        $this->_redis = new Redis();

        $persistent = $config['persistent'] ? 'pconnect' : 'connect';
        $this->_redis->$persistent($config['host'], $config['port'], $config['timeout']);

        if ($config['username'] && $config['password']) {
            $this->_redis->auth($config['username'], $config['password']);
        }

        if ($config['database'] > 0) {
            $this->_redis->select($config['database']);
        }
    }

    public function get($key) {
        $value = $this->_redis->get($key);
        if ($value === false) {
            return false;
        }

        if (isset($this->_serialized[$key])) {
            return unserialize($value);
        }

        return $value;
    }

    public function save($id, $data, $ttl = 60, $raw = false) {
        if (!$raw) {
            $data = serialize($data);
            $this->_serialized[$id] = true;
        }

        if ($ttl === 0) {
            $result = $this->_redis->set($id, $data);
        } else {
            $result = $this->_redis->setex($id, $ttl, $data);
        }

        return $result;
    }

    public function delete($key) {
        return $this->_redis->delete($key) > 0;
    }

    public function increment($id, $offset = 1) {
        return $this->_redis->incrby($id, $offset);
    }

    public function decrement($id, $offset = 1) {
        return $this->_redis->decrby($id, $offset);
    }

    public function clean() {
        $keys = $this->_redis->keys('*');
        if (empty($keys)) {
            return true;
        }
        return $this->_redis->del($keys) > 0;
    }

    public function cache_info($type = NULL) {
        return $this->_redis->info();
    }

    public function get_metadata($key) {
        $ttl = $this->_redis->ttl($key);
        return [
            'ttl' => $ttl,
            'serialized' => isset($this->_serialized[$key]),
        ];
    }

    public function is_supported() {
        return class_exists('Redis');
    }

    public function __destruct() {
        $this->_redis->close();
    }
}

