class HttpClientKernel implements HttpKernelInterface {
    private $client;

    public function __construct(?HttpClientInterface $client = null) {
        if ($client === null) {
            if (!class_exists('Symfony\\Component\\HttpClient\\HttpClient')) {
                throw new \RuntimeException('Symfony HttpClient component is not installed.');
            }
            $client = HttpClient::create();
        }
        $this->client = $client;
    }

    public function handle(Request $request, int $type = HttpKernelInterface::MAIN_REQUEST, bool $catch = true): Response {
        $method = $request->getMethod();
        $uri = $request->getUri();
        $headers = $this->getHeaders($request);
        $body = $this->getBody($request);

        $response = $this->client->request($method, $uri, [
            'headers' => $headers,
            'body' => $body,
        ]);

        $statusCode = $response->getStatusCode();
        $responseHeaders = $response->getHeaders();
        $content = $response->getContent();

        $flatHeaders = [];
        foreach ($responseHeaders as $name => $values) {
            $flatHeaders[$name] = implode(', ', $values);
        }

        return new Response($content, $statusCode, $flatHeaders);
    }

    private function getHeaders(Request $request): array {
        $headers = $request->headers->all();
        $cookies = $request->cookies->all();

        if (!empty($cookies)) {
            $headers['Cookie'] = http_build_query($cookies, '', '; ');
        }

        return $headers;
    }

    private function getBody(Request $request): ?AbstractPart {
        $method = $request->getMethod();
        if ($method === 'GET' || $method === 'HEAD') {
            return null;
        }

        $body = $request->getContent();

        if ($body === null) {
            return null;
        }

        if (is_array($body)) {
            if (!class_exists('Symfony\\Component\\Mime\\FormDataPart')) {
                throw new \RuntimeException('Mime component is required for form data.');
            }
            return new FormDataPart($body);
        } else {
            if (!class_exists('Symfony\\Component\\Mime\\TextPart')) {
                throw new \RuntimeException('Mime component is required for raw content.');
            }
            return new TextPart($body);
        }
    }
}
