        return null;
    }

    $info = stream_get_contents($pipes[1]);
    fclose($pipes[1]);
    fclose($pipes[2]);
    proc_close($process);

    if ($cp) {
        sapi_windows_cp_set($cp);
    }

    return $info;
}

