class DefaultPhpProcess {
    private $tempFile;
    private $stdin;
    private $env;
    private $timeout;

    public function runJob(string $job, array $settings = []): array {
        $this->stdin = $job;
        $this->env = $settings['env'] ?? $this->env;
        $this->timeout = $settings['timeout'] ?? $this->timeout;
        return $this->runProcess($job, $settings);
    }

    public function getHandles(): array {
        return [];
    }

    protected function runProcess(string $job, array $settings): array {
        $descriptorspec = [
            0 => ['pipe', 'w'],
            1 => ['pipe', 'r'],
            2 => ['pipe', 'r'],
        ];

        $tempFile = null;
        if ($this->useTemporaryFile()) {
            $tempFile = tempnam(sys_get_temp_dir(), 'php_proc');
            file_put_contents($tempFile, $job);
            $descriptorspec[0] = ['file', $tempFile, 'r'];
        }

        $process = proc_open($job, $descriptorspec, $pipes, null, $this->env);

        if (!$process) {
            throw new Exception("Failed to start process");
        }

        if (!$this->useTemporaryFile()) {
            $this->process($pipes[0], $job);
        }

        $timeout = $settings['timeout'] ?? $this->timeout;
        $startTime = microtime(true);

        while (true) {
            $status = proc_get_status($process);
            if (!$status['running']) {
                break;
            }
            if (microtime(true) - $startTime > $timeout) {
                proc_terminate($process);
                break;
            }
            usleep(100000);
        }

        $stdout = stream_get_contents($pipes[1]);
        $stderr = stream_get_contents($pipes[2]);

        fclose($pipes[0]);
        fclose($pipes[1]);
        fclose($pipes[2]);
        proc_close($process);

        if ($tempFile) {
            $this->tempFile = $tempFile;
        }

        return ['stdout' => $stdout, 'stderr' => $stderr];
    }

    protected function process($pipe, $job) {
        fwrite($pipe, $job);
        fclose($pipe);
    }

    public function cleanup() {
        if ($this->tempFile && file_exists($this->tempFile)) {
            unlink($this->tempFile);
        }
    }

    protected function useTemporaryFile(): bool {
        return strlen($this->stdin) > 1024;
    }
}
