use Symfony\Component\HttpClient\Part\FormDataPart;
use Symfony\Component\HttpClient\Part\TextPart;
use Symfony\Component\HttpKernel\HttpKernelInterface;

class HttpClientKernel {
    private HttpClientInterface $client;

    public function __construct(HttpClientInterface $client = null) {
        $this->client = $client ?: HttpClient::create();
    }

    public function handle(Request $request, int $type = HttpKernelInterface::MAIN_REQUEST, bool $catch = true): Response {
        $headers = $this->getHeaders($request);
        $body = $this->getBody($request);

        try {
            $response = $this->client->request($request->getMethod(), $request->getUri(), [
                'headers' => $headers,
                'body' => $body,
            ]);
        } catch (\Exception $e) {
            if ($catch) {
                return new Response('Error: ' . $e->getMessage(), 500);
            } else {
                throw $e;
            }
        }

        return $response;
    }

    private function getBody(Request $request): ?\Symfony\Component\HttpClient\Part\AbstractPart {
        if ($request->isMethod('GET') || $request->isMethod('HEAD')) {
            return null;
        }

        $body = $request->getContent();
        if (empty($body)) {
            return null;
        }

        $contentType = $request->headers->get('Content-Type');
        if (strpos($contentType, 'application/x-www-form-urlencoded') !== false || strpos($contentType, 'multipart/form-data') !== false) {
            $formData = new FormDataPart();
            $formData->setFields($request->request->all());
            $formData->setFiles($request->files->all());
            return $formData;
        } else {
            return new TextPart($body);
        }
    }

    private function getHeaders(Request $request): array {
        $headers = $request->headers->all();
        $cookies = $request->cookies->all();
        if (!empty($cookies)) {
            $headers['Cookie'] = http_build_query($cookies, '', '; ');
        }
        return $headers;
    }
