function http_request($url, $method = 'GET', $data = false, $headers = false, $options = false, $json = false, $timeout = 3600) {
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_HEADER, true);
    curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);
    curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

    if ($method !== 'GET' && $data !== false) {
        if (is_array($data)) {
            $data = json_encode($data);
        }
        curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
    } elseif ($method === 'GET' && $data !== false) {
        if (is_array($data)) {
            $url .= '?' . http_build_query($data);
        } else {
            $url .= '?' . $data;
        }
    }

    if ($headers !== false) {
        if (is_array($headers)) {
            $headerArray = [];
            foreach ($headers as $key => $value) {
                $headerArray[] = "$key: $value";
            }
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headerArray);
        } else {
            curl_setopt($ch, CURLOPT_HTTPHEADER, explode("\r\n", $headers));
        }
    }

    if ($options !== false) {
        foreach ($options as $option => $value) {
            curl_setopt($ch, $option, $value);
        }
    }

    $response = curl_exec($ch);
    $error = curl_error($ch);
    $info = curl_getinfo($ch);
    curl_close($ch);

    if ($error) {
        return [
            'data' => $error,
            'status' => false,
            'code' => 0,
            'header' => $info
        ];
    }

    $headerSize = $info['header_size'];
    $headerStr = substr($response, 0, $headerSize);
    $bodyStr = substr($response, $headerSize);

    $headers = [];
    $headerLines = explode("\r\n", $headerStr);
    foreach ($headerLines as $line) {
        if (strpos($line, ':') !== false) {
            list($key, $value) = explode(':', $line, 2);
            $headers[trim($key)] = trim($value);
        }
    }

    $data = $bodyStr;
    if ($json && is_string($data)) {
        $decoded = json_decode($data, true);
        if (json_last_error() === JSON_ERROR_NONE) {
            $data = $decoded;
        }
    }

    $status = ($info['http_code'] >= 200 && $info['http_code'] < 300);
    return [
        'data' => $data,
        'status' => $status,
        'code' => $info['http_code'],
        'header' => $headers
    ];
}
