        return [];
    }

    protected function runProcess(string $job, array $settings): array {
        $descriptorspec = [
            0 => ['pipe', 'r'],
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w'],
        ];

        $command = 'php';
        $this->tempFile = null;

        if ($this->useTemporaryFile()) {
            $this->tempFile = tempnam(sys_get_temp_dir(), 'phpjob');
            file_put_contents($this->tempFile, $job);
            $command = 'php ' . escapeshellarg($this->tempFile);
        } else {
            $command = 'php';
        }

        $process = proc_open($command, $descriptorspec, $pipes, null, $this->env);

        if (!is_resource($process)) {
            throw new Exception('Failed to start process');
        }

        if (!$this->useTemporaryFile()) {
            $this->process($pipes[0], $job);
            fclose($pipes[0]);
        }

        $stdout = stream_get_contents($pipes[1]);
        fclose($pipes[1]);

        $stderr = stream_get_contents($pipes[2]);
        fclose($pipes[2]);

        $startTime = microtime(true);
        $status = proc_get_status($process);
        while ($status['running']) {
            usleep(100000);
            $status = proc_get_status($process);
            if (microtime(true) - $startTime > $this->timeout) {
                proc_terminate($process);
                $exitCode = -1;
                break;
            }
        }

        $exitCode = $status['exitcode'];
        proc_close($process);

        return ['output' => $stdout, 'error' => $stderr, 'exitcode' => $exitCode];
    }

    protected function process(resource $pipe, string $job): void {
        fwrite($pipe, $job);
    }

    public function cleanup(): void {
        if ($this->tempFile && file_exists($this->tempFile)) {
            unlink($this->tempFile);
        }
    }

    protected function useTemporaryFile(): bool {
