class QiniuStorage {
    const QINIU_RSF_HOST = 'http://rsf.qiniu.com';
    const QINIU_RS_HOST = 'http://rs.qiniu.com';
    const QINIU_UP_HOST = 'http://up.qiniu.com';
    protected $timeout = 30;
    protected $error = '';
    protected $errorStr = '';

    public function request($path, $method, $headers = null, $body = null) {
        $host = self::QINIU_RS_HOST;
        if (strpos($path, 'http') === 0) {
            $url = $path;
        } else {
            $url = $host . $path;
        }

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

        $defaultHeaders = [
            'Content-Type: application/x-www-form-urlencoded',
            'User-Agent: QiniuPHP'
        ];
        if ($headers) {
            $headers = array_merge($defaultHeaders, $headers);
        } else {
            $headers = $defaultHeaders;
        }
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);

        if ($body !== null) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        }

        $response = curl_exec($ch);
        if ($response === false) {
            $this->error = 'Curl error: ' . curl_error($ch);
            $this->errorStr = curl_errno($ch);
            curl_close($ch);
            return false;
        }

        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $headersSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headerText = substr($response, 0, $headersSize);
        $bodyText = substr($response, $headersSize);

        curl_close($ch);

        if ($httpCode >= 200 && $httpCode < 300) {
            return $this->response($response);
        } else {
            $this->error($headerText, $bodyText);
            return false;
        }
    }

    public function response($text) {
        $headers = [];
        $body = '';
        $lines = explode("\r\n", $text);
        $inHeader = true;
        foreach ($lines as $line) {
            if ($inHeader && $line === '') {
                $inHeader = false;
                continue;
            }
            if ($inHeader) {
                $headers[] = $line;
            } else {
                $body .= $line . "\r\n";
            }
        }

        $json = [];
        foreach ($headers as $header) {
            if (strpos($header, 'Content-Type: application/json') !== false) {
                $json = json_decode(trim($body), true);
                break;
            }
        }
        return $json ?: $body;
    }

    public function error($header, $body) {
        $lines = explode("\r\n", $header);
        $statusCode = 0;
        foreach ($lines as $line) {
            if (strpos($line, 'HTTP/') === 0) {
                $parts = explode(' ', $line);
                $statusCode = (int)$parts[1];
                break;
            }
        }
        $this->error = "HTTP Error $statusCode";
        $this->errorStr = $body;
    }
}
