class URLChecker {
    private const POLL_INTERVAL_MS = 1000;
    private const CONNECT_TIMEOUT_MS = 5000;

    public function waitUntilAvailable(int $timeout_in_ms, string $url): self {
        $start = microtime(true) * 1000;
        $timeout = $timeout_in_ms;
        while (true) {
            $current = microtime(true) * 1000;
            if ($current - $start > $timeout) {
                throw new \TimeoutException("Timeout waiting for URL to be available");
            }
            $code = $this->getHTTPResponseCode($url);
            if ($code === 200) {
                break;
            }
            usleep($this->POLL_INTERVAL_MS * 1000);
        }
        return $this;
    }

    public function waitUntilUnavailable(int $timeout_in_ms, string $url): self {
        $start = microtime(true) * 1000;
        $timeout = $timeout_in_ms;
        while (true) {
            $current = microtime(true) * 1000;
            if ($current - $start > $timeout) {
                throw new \TimeoutException("Timeout waiting for URL to be unavailable");
            }
            $code = $this->getHTTPResponseCode($url);
            if ($code !== 200) {
                break;
            }
            usleep($this->POLL_INTERVAL_MS * 1000);
        }
        return $this;
    }

    private function getHTTPResponseCode(string $url): ?int {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $this->CONNECT_TIMEOUT_MS / 1000);
        $response = curl_exec($ch);
        if ($response === false) {
            curl_close($ch);
            return null;
        }
        $code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        return $code;
    }
}
