            throw new InvalidArgumentException("Either outBizBo or orderId must be provided.");
        }

        if (!$this->checkEmpty($outBizBo)) {
            $params['out_biz_no'] = $outBizBo;
        } else {
            $params['order_id'] = $orderId;
        }

        $sign = $this->generateSign($params);
        $params['sign'] = $sign;

        $url = 'https://openapi.alipay.com/gateway.do';
        $response = $this->curlPost($url, $params);
        return json_decode($response, true);
    }

    private function generateSign(array $params, $signType = 'RSA') {
        $signContent = $this->getSignContent($params);
        return $this->sign($signContent, $signType);
    }

    private function sign($data, $signType = 'RSA') {
        if ($signType === 'RSA') {
            $privateKey = openssl_pkey_get_private($this->rsaPrivateKey);
            if (!$privateKey) {
                throw new Exception('Failed to get private key.');
            }
            openssl_sign($data, $signature, $privateKey, OPENSSL_ALGO_SHA256);
            openssl_free_key($privateKey);
            return base64_encode($signature);
        }
        throw new Exception("Unsupported sign type: $signType");
    }

    private function checkEmpty($value) {
        return is_null($value) || $value === '';
    }

    private function getSignContent(array $params) {
        ksort($params);
        $signContent = '';
        foreach ($params as $key => $value) {
            if ($this->checkEmpty($value)) {
                continue;
            }
            $value = $this->characet($value, $this->charset);
            $signContent .= $key . '=' . $value . '&';
        }
        return rtrim($signContent, '&');
    }

    private function characet($data, $targetCharset) {
        if (function_exists('mb_convert_encoding')) {
            return mb_convert_encoding($data, $targetCharset, $this->charset);
        } else {
            return iconv($this->charset, $targetCharset, $data);
        }
    }

    private function curlPost($url = '', $postData = [], $options = []) {
        if (empty($url)) {
            throw new InvalidArgumentException("URL is required.");
