class Image {
    const BASE_URL = 'https://example.com/placeholders/';
    const FORMAT_JPG = 'jpg';
    const FORMAT_JPEG = 'jpeg';
    const FORMAT_PNG = 'png';
    protected $categories = ['nature', 'people', 'technology', 'animals'];

    public function image(
        ?string $dir = null,
        int $width = 640,
        int $height = 480,
        ?string $category = null,
        bool $fullPath = true,
        bool $randomize = true,
        ?string $word = null,
        bool $gray = false,
        string $format = 'png'
    ): string|false {
        $url = $this->imageUrl($width, $height, $category, $randomize, $word, $gray, $format);
        $pathInfo = parse_url($url, PHP_URL_PATH);
        $filename = basename($pathInfo);

        if ($dir === null) {
            $dir = 'images/';
        }

        if (!is_dir($dir)) {
            mkdir($dir, 0777, true);
        }

        $filePath = $dir . $filename;

        $content = $this->downloadImage($url);
        if ($content === false) {
            return false;
        }

        if (file_put_contents($filePath, $content) === false) {
            return false;
        }

        return $fullPath ? $filePath : $filename;
    }

    protected function downloadImage(string $url): string|false {
        if (function_exists('curl_init')) {
            $ch = curl_init();
            curl_setopt($ch, CURLOPT_URL, $url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            $content = curl_exec($ch);
            curl_close($ch);
            return $content;
        } else {
            return file_get_contents($url);
        }
    }

    public function imageUrl(
        int $width,
        int $height,
        ?string $category,
        bool $randomize,
        ?string $word,
        bool $gray,
        string $format
    ): string {
        $parts = [];
        $parts[] = $width . 'x' . $height;

        if ($category !== null && in_array($category, $this->categories)) {
            $parts[] = $category;
        }

        if ($word !== null) {
            $parts[] = $word;
        }

        $path = implode('/', $parts);
        $query = [];

        if ($randomize) {
            $query['randomize'] = '1';
        }

        if ($gray) {
            $query['gray'] = '1';
        }

        $queryString = http_build_query($query);

        return self::BASE_URL . $path . '.' . $format . ($queryString ? '?' . $queryString : '');
    }
}

