    }

    public static function parseHeaders(string $raw): array {
        $headers = [];
        $lines = explode("\r\n", $raw);
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $key = self::ucwordsHyphen(trim($key));
                $value = trim($value);
                if (isset($headers[$key])) {
                    if (!is_array($headers[$key])) {
                        $headers[$key] = [$headers[$key]];
                    }
                    $headers[$key][] = $value;
                } else {
                    $headers[$key] = $value;
                }
            }
        }
        return $headers;
    }

    public static function escapeQuotes(string $str): string {
        return str_replace('"', '\\"', $str);
    }

    public static function ucwordsHyphen(string $str): string {
        $parts = explode('-', $str);
        $parts = array_map('ucfirst', $parts);
        return implode('-', $parts);
    }

    public static function get(string $url, array $headers = []): Response {
        $request = new Request($url, 'GET', null, $headers);
        return self::sendRequest($request);
    }

    public static function delete(string $url, array $headers = []): Response {
        $request = new Request($url, 'DELETE', null, $headers);
        return self::sendRequest($request);
    }

    public static function post(string $url, mixed $body, array $headers = []): Response {
        $request = new Request($url, 'POST', $body, $headers);
        return self::sendRequest($request);
    }

    public static function multipartPost(string $url, array $fields, string $name, string $fileName, string $fileBody, string $mimeType = null, array $headers = []): Response {
        $boundary = '----WebKitFormBoundary' . md5(time());
        $body = '';

        foreach ($fields as $key => $value) {
            $body .= "--$boundary\r\n";
            $body .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
            $body .= $value . "\r\n";
        }

        $body .= "--$boundary\r\n";
        $body .= "Content-Disposition: form-data; name=\"$name\"; filename=\"$fileName\"\r\n";
        $body .= "Content-Type: $mimeType\r\n\r\n";
        $body .= $fileBody . "\r\n";
        $body .= "--$boundary--\r\n";

        $headers[] = "Content-Type: multipart/form-data; boundary=$boundary";

        $request = new Request($url, 'POST', $body, $headers);
        return self::sendRequest($request);
    }

    public static function sendRequest(Request $request): Response {
        $ch = curl_init();
        $url = $request->getUrl();
        $method = $request->getMethod();
        $body = $request->getBody();
        $headers = $request->getHeaders();

        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        if ($body !== null) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        }

        curl_setopt($ch, CURLOPT_HEADER, true);
        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headerStr = substr($response, 0, $headerSize);
        $bodyStr = substr($response, $headerSize);

        $headersArray = self::parseHeaders($headerStr);

        if ($response === false) {
            $error = curl_error($ch);
            curl_close($ch);
            throw new Exception("cURL error: $error");
        }

        curl_close($ch);

        return new Response($bodyStr, $httpCode, $headersArray);
    }
}

class Request {
