        $arr = array();
        foreach ($tmp as $k=>$v) {
            $v = trim($v);
            if ($v!="")
                $arr[]= "." . $v;
        }

        $seconddomain = "";
        $selfdomain   = "";
        $rootdomain   = "";
        foreach ($arr as $k => $v) {
            $pos = stripos($domain, $v);
