class CurlDownloader {
    private ?string $tempDir;
    private ?string $outputFile;

    public function __construct() {
        $this->tempDir = null;
        $this->outputFile = null;
    }

    public function setTempDir(string $tempDir): void {
        $this->tempDir = $tempDir;
    }

    public function download(string $url): bool {
        $tempDir = $this->tempDir ?: sys_get_temp_dir();
        $filename = tempnam($tempDir, 'curl_download_');
        if (!$filename) {
            throw new ErrorException("Failed to create temporary file.");
        }

        $ch = curl_init($url);
        if (!$ch) {
            throw new ErrorException("Failed to initialize cURL.");
        }

        $fileHandle = fopen($filename, 'w');
        if (!$fileHandle) {
            unlink($filename);
            throw new ErrorException("Failed to open file for writing.");
        }

        curl_setopt_array($ch, [
            CURLOPT_RETURNTRANSFER => false,
            CURLOPT_FILE => $fileHandle,
            CURLOPT_TIMEOUT => 30,
            CURLOPT_FOLLOWLOCATION => true,
            CURLOPT_SSL_VERIFYPEER => false,
            CURLOPT_SSL_VERIFYHOST => 0,
        ]);

        $result = curl_exec($ch);
        $error = curl_error($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);

        curl_close($ch);
        fclose($fileHandle);

        if ($result === false || $httpCode >= 400) {
            unlink($filename);
            throw new ErrorException("cURL error: $error (HTTP Code: $httpCode)");
        }

        if (!file_exists($filename)) {
            unlink($filename);
            throw new ErrorException("Failed to download file.");
        }

        $this->outputFile = $filename;
        return true;
    }

    public function getFilename(): string {
        if ($this->outputFile === null) {
            throw new RuntimeException("No file downloaded yet.");
        }
        return $this->outputFile;
    }

    public function cleanup(): void {
        if ($this->outputFile !== null && file_exists($this->outputFile)) {
            unlink($this->outputFile);
            $this->outputFile = null;
        }
    }
}
