            'oauth_consumer_key' => $this->consumer_key,
            'oauth_token' => $this->oauth_access_token,
            'oauth_signature_method' => 'HMAC-SHA1',
            'oauth_timestamp' => time(),
            'oauth_nonce' => md5(uniqid(rand(), true)),
            'oauth_version' => '1.0',
        ];

        $params = array_merge($params, $this->oauth);

        $baseString = $this->buildBaseString($baseURI, $requestMethod, $params);

        $signingKey = rawurlencode($this->consumer_secret) . '&' . rawurlencode($this->oauth_access_token_secret);
        $signature = base64_encode(hash_hmac('sha1', $baseString, $signingKey, true));

        $this->oauth['oauth_signature'] = $signature;

        return $this;
    }

    private function buildBaseString($baseURI, $method, $params) {
        $encodedParams = [];
        foreach ($params as $key => $value) {
            $encodedParams[] = rawurlencode($key) . '=' . rawurlencode($value);
        }
        sort($encodedParams);
        return $method . '&' . rawurlencode($baseURI) . '&' . rawurlencode(implode('&', $encodedParams));
    }

    private function buildAuthorizationHeader(array $oauth) {
        $header = 'OAuth ';
        $values = [];
        foreach ($oauth as $key => $value) {
            $values[] = rawurlencode($key) . '="' . rawurlencode($value) . '"';
        }
        sort($values);
        return $header . implode(', ', $values);
    }

