        $ch = curl_init();
        $method = 'GET';
        if ($this->postfields) {
            $method = 'POST';
        }

        curl_setopt($ch, CURLOPT_URL, $this->url);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

        if ($method === 'POST' && $this->postfields) {
