    public static function multipartPost(string $url, array $fields, string $name, string $fileName, string $fileBody, string $mimeType = null, array $headers = []): Response {
        $boundary = '----WebKitFormBoundary' . md5(time());
        $body = '';

        foreach ($fields as $key => $value) {
            $body .= "--$boundary\r\n";
            $body .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
            $body .= $value . "\r\n";
        }

        $body .= "--$boundary\r\n";
        $body .= "Content-Disposition: form-data; name=\"$name\"; filename=\"$fileName\"\r\n";
        $body .= "Content-Type: $mimeType\r\n\r\n";
        $body .= $fileBody . "\r\n";
        $body .= "--$boundary--\r\n";

        $headers['Content-Type'] = "multipart/form-data; boundary=$boundary";

        return self::sendRequest($url, 'POST', $body, $headers);
    }

    public static function userAgent(): string {
        $sdk = 'MySDK/1.0';
        $system = php_uname('s') . '/' . php_uname('r');
        $phpVersion = 'PHP/' . phpversion();
        return "$sdk $system $phpVersion";
    }

    private static function parseHeaders(string $raw): array {
        $headers = [];
        $lines = explode("\r\n", $raw);
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $key = self::wordcHypen($key);
                $headers[trim($key)] = trim($value);
            }
        }
        return $headers;
    }

    private static function wordcHypen(string $str): string {
        return str_replace(' ', '-', ucwords(str_replace('-', ' ', $str)));
    }

    private static function escapeQuotes(string $str): string {
        return addcslashes($str, '"');
    }
