class Downloader {
    public static function start($url, $saveFile, $timeout = 10) {
        if (is_array($url)) {
            $results = [];
            foreach ($url as $u) {
                $results[] = self::start($u, $saveFile, $timeout);
            }
            return $results;
        }

        if (!filter_var($url, FILTER_VALIDATE_URL)) {
            return ['error' => 'Invalid URL'];
        }

        $supportsRange = self::checkRangeSupport($url, $timeout);
        if ($supportsRange) {
            return self::fileDownloadCurl($url, $saveFile, true);
        } else {
            return self::fileDownloadFopen($url, $saveFile);
        }
    }

    private static function checkRangeSupport($url, $timeout) {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_NOBODY, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($httpCode == 200) {
            $headers = self::parseHeaders($response);
            if (isset($headers['accept-ranges']) && strtolower($headers['accept-ranges']) === 'bytes') {
                return true;
            }
        }
        return false;
    }

    private static function parseHeaders($response) {
        $headers = [];
        $lines = explode("\r\n", $response);
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[trim($key)] = trim($value);
            }
        }
        return $headers;
    }

    public static function fileDownloadFopen($url, $fileName, $headerSize = 0) {
        $fp = fopen($url, 'rb');
        if (!$fp) {
            return ['error' => 'Unable to open URL'];
        }

        $file = fopen($fileName, 'wb');
        if (!$file) {
            fclose($fp);
            return ['error' => 'Unable to open local file'];
        }

        while (!feof($fp)) {
            $buffer = fread($fp, 8192);
            fwrite($file, $buffer);
        }

        fclose($fp);
        fclose($file);
        self::checkGzip($fileName);
        return ['success' => true, 'file' => $fileName];
    }

    public static function fileDownloadCurl($url, $fileName, $supportRange = false, $existsLength = 0, $length = 0) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, false);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);

        if ($supportRange) {
            $range = "bytes=" . $existsLength . "-";
            curl_setopt($ch, CURLOPT_RANGE, $range);
        }

        $file = fopen($fileName, 'ab');
        if (!$file) {
            curl_close($ch);
            return ['error' => 'Unable to open local file'];
        }

        curl_setopt($ch, CURLOPT_FILE, $file);
        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        fclose($file);

        if ($httpCode >= 200 && $httpCode < 300) {
            self::checkGzip($fileName);
            return ['success' => true, 'file' => $fileName];
        } else {
            return ['error' => 'Download failed with HTTP code ' . $httpCode];
        }
    }

    public static function checkGzip($file) {
        $handle = fopen($file, 'rb');
        if ($handle === false) {
            return;
        }
        $firstBytes = fread($handle, 2);
        fclose($handle);
        if ($firstBytes === "\x1F\x8B") {
            $content = file_get_contents($file);
            $decompressed = gzdecode($content);
            if ($decompressed !== false) {
                file_put_contents($file, $decompressed);
            }
        }
    }
