    protected $error;
    protected $config;
    protected $bcs;

    public function __construct(array $config) {
        $this->config = $config;
        $this->rootPath = $config['rootPath'] ?? '';
        $this->error = '';
        $this->bcs = null;
    }

    public function request($path, $method, $headers = null, $body = null) {
        $ch = curl_init();
        $url = 'http://' . $this->config['bucket'] . '.bcs.duapp.com' . $path;
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

        $headers = $headers ?? [];
        $headers['Date'] = gmdate('D, d M Y H:i:s');

        if ($body !== null) {
            $headers['Content-MD5'] = base64_encode(md5($body, true));
            $headers['Content-Type'] = 'application/octet-stream';
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        }

        $headers['Authorization'] = $this->generateAuthorizationHeader($method, $path, $body, $headers);

        $curlHeaders = [];
        foreach ($headers as $k => $v) {
            $curlHeaders[] = "$k: $v";
        }
        curl_setopt($ch, CURLOPT_HTTPHEADER, $curlHeaders);

        curl_setopt($ch, CURLOPT_TIMEOUT, $this->config['timeout'] ?? 30);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($response === false) {
            $this->error = curl_error($ch);
            return false;
        }

        if ($httpCode >= 200 && $httpCode < 300) {
            return $this->response($response);
        } else {
            $this->error($response);
            return false;
        }
    }

    private function response($text) {
        $data = json_decode($text, true);
        if (json_last_error() === JSON_ERROR_NONE) {
            return $data;
        } else {
            $this->error = 'Invalid JSON response';
            return false;
        }
    }

    private function error($responseText) {
        $data = $this->response($responseText);
        if ($data && isset($data['error'])) {
            $this->error = $data['error'];
        } else {
            $this->error = 'Unknown error';
        }
    }

    private function generateAuthorizationHeader($method, $path, $body, $headers) {
        $date = $headers['Date'];
        $contentMd5 = $headers['Content-MD5'] ?? '';
        $contentType = $headers['Content-Type'] ?? '';

        $stringToSign = "$method\n$contentMd5\n$contentType\n$date\n$path";

        $signature = hash_hmac('sha1', $stringToSign, $this->config['SecretKey'], true);
        $signatureBase64 = base64_encode($signature);

        return "BSC {$this->config['AccessKey']}:$signatureBase64";
    }
}
