        $this->curlDataWritten = 0;
        $this->lastURLError = false;
    }

    public function curlWrite($h, $d) {
        if ($this->curlFH === false) {
            return 0;
        }
        $bytesWritten = fwrite($this->curlFH, $d);
        $this->curlDataWritten += $bytesWritten;
        $maxSize = 10485760; // 10MB
        if ($this->curlDataWritten > $maxSize) {
            return 0;
        }
        return $bytesWritten;
    }

