                $this->lastURLError = "cURL error: $curlErrStr";
                fclose($this->curlFH);
                return false;
            }

            if ($httpCode >= 400) {
                $this->lastURLError = "HTTP error: $httpCode";
                fclose($this->curlFH);
                return false;
            }

            fclose($this->curlFH);
            return true;
        } else {
            $context = stream_context_create([
                'http' => [
