class File {
    public $url;
    public $useragent;
    public $success;
    public $headers;
    public $body;
    public $status_code;
    public $redirects;
    public $error;
    public $method;
    public $permanent_url;
    public $permanentUrlMutable;

    public function __construct(string $url, int $timeout = 10, int $redirects = 5, ?array $headers = null, ?string $useragent = null, bool $force_fsockopen = false, array $curl_options = []) {
        $this->permanent_url = $url;
        $this->url = $url;
        $this->redirects = 0;
        $this->error = '';
        $this->success = false;
        $this->status_code = 0;
        $this->body = '';
        $this->headers = [];
        $this->method = 0;
        $this->permanentUrlMutable = true;

        $max_redirects = $redirects;
        $current_url = $url;
        $current_redirects = 0;

        while ($current_redirects < $max_redirects) {
            $ch = curl_init();

            curl_setopt($ch, CURLOPT_URL, $current_url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);
            curl_setopt($ch, CURLOPT_HEADER, true);
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, false);

            if ($useragent) {
                curl_setopt($ch, CURLOPT_USERAGENT, $useragent);
            }

            if ($headers) {
                curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
            }

            foreach ($curl_options as $option => $value) {
                curl_setopt($ch, $option, $value);
            }

            $response = curl_exec($ch);
            $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            $effective_url = curl_getinfo($ch, CURLINFO_EFFECTIVE_URL);

            if ($response === false) {
                $this->error = curl_error($ch);
                $this->success = false;
                curl_close($ch);
                break;
            }

            $header_size = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
            $headers_str = substr($response, 0, $header_size);
            $body = substr($response, $header_size);

            $this->headers = $this->parseHeaders($headers_str);

            if ($http_code >= 300 && $http_code < 400) {
                $location = null;
                if (preg_match('/Location:\s*(.*)/i', $headers_str, $matches)) {
                    $location = trim($matches[1]);
                }

                if ($location) {
                    if (filter_var($location, FILTER_VALIDATE_URL) === false) {
                        $parsed = parse_url($current_url);
                        $base = $parsed['scheme'] . '://' . $parsed['host'] . (isset($parsed['port']) ? ':' . $parsed['port'] : '');
                        $location = $base . $location;
                    }
                    $current_url = $location;
                    $current_redirects++;
                    $this->redirects = $current_redirects;
                    curl_close($ch);
                    continue;
                } else {
                    $this->error = 'Redirect without Location header';
                    $this->success = false;
                    curl_close($ch);
                    break;
                }
            } else {
                $this->status_code = $http_code;
                $this->body = $body;
                $this->url = $effective_url;
                $this->success = ($http_code >= 200 && $http_code < 300);
                curl_close($ch);
                break;
            }
        }

        if ($this->success === false && $this->error === '') {
            $this->error = 'Max redirects exceeded';
        }
    }

    private function parseHeaders($headers_str) {
        $headers = [];
        $lines = explode("\r\n", $headers_str);
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[trim($key)] = trim($value);
            }
        }
        return $headers;
    }

    public function get_body_content(): string {
        return $this->body;
    }

    public function get_headers(): array {
        return $this->headers;
    }

    public function get_status_code(): int {
        return $this->status_code;
    }

    public function get_final_requested_uri(): string {
        return $this->url;
    }

    public function get_permanent_uri(): string {
        return $this->permanent_url;
    }
}

