        $this->disktag = $tag;
        $this->error = [];
    }

    public function GDAPI($method, $url, $data = '') {
        if (empty($this->access_token)) {
            $this->error[] = 'Access token not available.';
            return false;
        }

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        curl_setopt($ch, CURLOPT_HTTPHEADER, [
            'Authorization: Bearer ' . $this->access_token,
            'Content-Type: application/json'
        ]);

        if ($data) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
        }

        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        $response = curl_exec($ch);

        if ($response === false) {
            $this->error[] = 'cURL error: ' . curl_error($ch);
            curl_close($ch);
            return false;
        }

        curl_close($ch);

        $result = json_decode($response, true);
        if (json_last_error() !== JSON_ERROR_NONE) {
            $this->error[] = 'JSON decode error: ' . json_last_error_msg();
            return false;
        }

        return $result;
    }

    public function get_access_token($refresh_token) {
        $url = $this->oauth_url;
        $data = [
            'client_id' => $this->client_id,
            'client_secret' => $this->client_secret,
            'refresh_token' => $refresh_token,
            'grant_type' => 'refresh_token'
        ];

        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HTTPHEADER, ['Content-Type: application/x-www-form-urlencoded']);

        $response = curl_exec($ch);
        curl_close($ch);

        if ($response === false) {
            $this->error[] = 'Failed to get access token.';
            return false;
        }

        $result = json_decode($response, true);
        if (isset($result['access_token'])) {
            $this->access_token = $result['access_token'];
            return true;
        } else {
            $this->error[] = 'Invalid response from OAuth server: ' . print_r($result, true);
            return false;
        }
    }
}

