        }
        $query = array_merge($query, $params);
        $parsed['query'] = http_build_query($query);
        $this->url = $this->buildUrlFromParts($parsed);
        return $this;
    }

    private function buildUrlFromParts(array $parts): string {
        $url = '';
        if (isset($parts['scheme'])) {
