function getWeChatArticleInfo($url) {
    if (!filter_var($url, FILTER_VALIDATE_URL) || strpos($url, 'mp.weixin.qq.com') === false) {
        return ['title' => '', 'cover' => '', 'success' => false];
    }

    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
    $html = curl_exec($ch);
    curl_close($ch);

    if (!$html) {
        return ['title' => '', 'cover' => '', 'success' => false];
    }

    preg_match('/<h1[^>]*>(.*?)<\/h1>/i', $html, $titleMatches);
    $title = $titleMatches[1] ?? '';

    preg_match('/<meta[^>]+property="og:image"[^>]+content="([^"]+)"/i', $html, $coverMatches);
    $coverUrl = $coverMatches[1] ?? '';

    $processedCover = '';
    if ($coverUrl) {
        $processedCover = 'https://weserv.nl/' . urlencode($coverUrl);
    }

    $success = !empty(trim($title)) && !empty($processedCover);
    return [
        'title' => trim($title),
        'cover' => $processedCover,
        'success' => $success
    ];
}
