class OpenAi {
    protected $engine = "davinci";
    protected $model = "text-davinci-002";
    protected $chatModel = "gpt-3.5-turbo";
    protected $headers = [];
    protected $contentTypes = ['json' => 'application/json', 'form' => 'multipart/form-data'];
    protected $timeout = 0;
    protected $stream_method = null;
    protected $customUrl = '';
    protected $proxy = '';
    protected $curlInfo = [];

    public function __construct($OPENAI_API_KEY) {
        $this->headers = [
            'Authorization' => 'Bearer ' . $OPENAI_API_KEY,
            'Content-Type' => $this->contentTypes['json']
        ];
    }

    public function sendRequest($url, $method, $opts = []) {
        $this->baseUrl($url);

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, false);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);

        $headers = [];
        foreach ($this->headers as $key => $value) {
            if ($key !== 'Content-Type') {
                $headers[] = "$key: $value";
            }
        }

        $content_type = $opts['content_type'] ?? 'json';
        $headers[] = "Content-Type: " . ($content_type === 'json' ? $this->contentTypes['json'] : $this->contentTypes['form']);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);

        if ($method !== 'GET') {
            curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        }

        $data = $opts['data'] ?? '';
        if ($content_type === 'json') {
            curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($data));
        } else {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
        }

        if ($this->proxy) {
            curl_setopt($ch, CURLOPT_PROXY, $this->proxy);
        }

        $stream = $opts['stream'] ?? false;
        if ($stream) {
            curl_setopt($ch, CURLOPT_WRITEFUNCTION, function($ch, $str) {
                if ($this->stream_method) {
                    call_user_func($this->stream_method, $str);
                }
                return strlen($str);
            });
        }

        $response = curl_exec($ch);
        $this->curlInfo = curl_getinfo($ch);
        $this->curlInfo['error'] = curl_error($ch);

        curl_close($ch);

        if ($response === false) {
            return false;
        }

        return $stream ? true : $response;
    }

    protected function baseUrl(&$url) {
        if ($this->customUrl) {
            $url = $this->customUrl . ltrim($url, '/');
        }
    }
}

