        $this->client = new Client();
    }

    public function triggerWebhook($data) {
        $method = $data['method'];
        $endpoint = $data['endpoint'];
        $headers = $data['headers'];
        $payload = $data['payload'];

        $formattedHeaders = $this->formatHeaders($headers);
        $formattedPayload = $this->formatPayload($payload);

        try {
            $response = $this->client->request($method, $endpoint, [
                'headers' => $formattedHeaders,
                'body' => is_array($formattedPayload) ? json_encode($formattedPayload) : $formattedPayload
            ]);
            $content = $response->getBody()->getContents();
            return ['status' => 'success', 'content' => $content];
        } catch (RequestException $e) {
            return ['status' => 'error', 'message' => $e->getMessage()];
        }
    }

    public function formatHeaders(array $headers): array {
