class BackendService {
    private function setCurl(string $backendUrl, string $backendToken, string $operation, string $id): array {
        $url = '';
        $method = '';
        $postFields = '';

        switch ($operation) {
            case 'add':
                $url = $backendUrl . '/tasks';
                $method = 'POST';
                $postFields = json_encode(['id' => $id]);
                break;
            case 'restart':
                $url = $backendUrl . '/tasks/restart';
                $method = 'POST';
                $postFields = json_encode(['id' => $id]);
                break;
            case 'remove':
                $url = $backendUrl . '/tasks/' . $id;
                $method = 'DELETE';
                $postFields = '';
                break;
            default:
                return ['status' => 'error', 'message' => 'Invalid operation'];
        }

        $ch = curl_init();

        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        curl_setopt($ch, CURLOPT_HTTPHEADER, [
            'Authorization: Bearer ' . $backendToken,
            'Content-Type: application/json'
        ]);

        if ($method === 'POST') {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $postFields);
        }

        $response = curl_exec($ch);

        if ($response === false) {
            $error = curl_error($ch);
            curl_close($ch);
            return ['status' => 'error', 'message' => 'cURL error: ' . $error];
        }

        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        $data = json_decode($response, true);

        if ($httpCode >= 200 && $httpCode < 300) {
            return ['status' => 'success', 'message' => $data['message'] ?? 'Operation successful'];
