function smarty_function_fetch($params, $template)
{
    if (empty($params['file'])) {
        trigger_error("[plugin] fetch parameter 'file' cannot be empty",E_USER_NOTICE);
        return;
    }

    if (stripos($params['file'], 'file://') === 0) {
        $params['file'] = substr($params['file'], 7);
    }

    $protocol = strpos($params['file'], '://');
    if ($protocol !== false) {
        $protocol = strtolower(substr($params['file'], 0, $protocol));
    }

    if (isset($template->smarty->security_policy)) {
        if ($protocol) {
            if (!$template->smarty->security_policy->isTrustedUri($params['file'])) {
                return;
            }
        } else {
            if (!$template->smarty->security_policy->isTrustedResourceDir($params['file'])) {
                return;
            }
        }
    }

    $content = '';
    if ($protocol == 'http') {
        if ($uri_parts = parse_url($params['file'])) {
            $host = $server_name = $uri_parts['host'];
            $timeout = 30;
            $port = empty($uri_parts['port']) ? 80 : $uri_parts['port'];
            $user = !empty($uri_parts['user']) ? $uri_parts['user'] : '';
            $pass = !empty($uri_parts['pass']) ? $uri_parts['pass'] : '';
            $uri = !empty($uri_parts['path']) ? $uri_parts['path'] : '/';
            $uri .= !empty($uri_parts['query']) ? '?' . $uri_parts['query'] : '';
            $_is_proxy = false;

            foreach ($params as $param_key => $param_value) {
                switch ($param_key) {
                    case "file":
                    case "assign":
                    case "assign_headers":
                        break;
                    case "user":
                        if (!empty($param_value)) $user = $param_value;
                        break;
                    case "pass":
                        if (!empty($param_value)) $pass = $param_value;
                        break;
                    case "proxy_host":
                        if (!empty($param_value)) $proxy_host = $param_value;
                        break;
                    case "proxy_port":
                        if (!preg_match('!\D!', $param_value)) {
                            $proxy_port = (int) $param_value;
                        }
                        break;
                    default:
                        break;
                }
            }

            if (!empty($proxy_host) && !empty($proxy_port)) {
                $_is_proxy = true;
                $fp = fsockopen($proxy_host,$proxy_port,$errno,$errstr,$timeout);
            } else {
                $fp = fsockopen($server_name,$port,$errno,$errstr,$timeout);
            }

            if (!$fp) {
                trigger_error("[plugin] unable to fetch: $errstr ($errno)",E_USER_NOTICE);
                return;
            } else {
                if ($_is_proxy) {
                    fputs($fp, 'GET ' . $params['file'] . " HTTP/1.0\r\n");
                } else {
                    fputs($fp, "GET $uri HTTP/1.0\r\n");
                }
                fputs($fp, "Host: $host\r\n");
                if (!empty($user) && !empty($pass)) {
                    fputs($fp, "Authorization: BASIC ".base64_encode("$user:$pass")."\r\n");
                }
                fputs($fp, "\r\n");

                while (!feof($fp)) {
                    $content .= fgets($fp,4096);
                }
                fclose($fp);
                $csplit = preg_split("!\r\n\r\n!",$content,2);
                $content = $csplit[1];
            }
        } else {
            trigger_error("[plugin fetch] unable to parse URL, check syntax",E_USER_NOTICE);
            return;
        }
    } else {
        $content = @file_get_contents($params['file']);
    }

    if (!empty($params['assign'])) {
        $template->assign($params['assign'], $content);
    } else {
        return $content;
    }
}

class CI_Trackback {
    public $charset = 'UTF-8';
    public $data = array(
        'url' => '',
        'title' => '',
        'excerpt' => '',
        'blog_name' => '',
        'charset' => ''
    );
    public $convert_ascii = TRUE;
    public $response = '';
    public $error_msg = array();

    public function process($url, $data) {
        $target = parse_url($url);

        if ( ! $fp = @fsockopen($target['host'], 80)) {
            $this->set_error('Invalid Connection: '.$url);
            return FALSE;
        }

        $path = isset($target['path']) ? $target['path'] : $url;
        empty($target['query']) OR $path .= '?'.$target['query'];

        if ($id = $this->get_id($url)) {
            $data = 'tb_id='.$id.'&'.$data;
        }

        fputs($fp, 'POST '.$path." HTTP/1.0\r\n");
        fputs($fp, 'Host: '.$target['host']."\r\n");
        fputs($fp, "Content-type: application/x-www-form-urlencoded\r\n");
        fputs($fp, 'Content-length: '.strlen($data)."\r\n");
        fputs($fp, "Connection: close\r\n\r\n");
        fputs($fp, $data);

        $this->response = '';
        while ( ! feof($fp)) {
            $this->response .= fgets($fp, 128);
        }
        @fclose($fp);

        if (stripos($this->response, '<error>0</error>') === FALSE) {
            $message = preg_match('/<message>(.*?)<\/message>/is', $this->response, $match)
                ? trim($match[1])
                : 'An unknown error was encountered';
            $this->set_error($message);
            return FALSE;
        }

        return TRUE;
    }

    public function get_id($url) {
        $tb_id = '';

        if (strpos($url, '?') !== FALSE) {
            $tb_array = explode('/', $url);
            $tb_end   = $tb_array[count($tb_array)-1];

            if ( ! is_numeric($tb_end)) {
                $tb_end  = $tb_array[count($tb_array)-2];
            }

            $tb_array = explode('=', $tb_end);
            $tb_id    = $tb_array[count($tb_array)-1];
        }
        else {
            $url = rtrim($url, '/');

            $tb_array = explode('/', $url);
            $tb_id    = $tb_array[count($tb_array)-1];

            if ( ! is_numeric($tb_id)) {
                $tb_id = $tb_array[count($tb_array)-2];
            }
        }

        return ctype_digit((string) $tb_id) ? $tb_id : FALSE;
    }

    public function set_error($msg) {
        $this->error_msg[] = $msg;
    }
}

