        $this->_request_authenticator = random_bytes(16);
    }

    public function DecodeAttribute($attribute_raw_value, $attribute_type) {
        switch ($attribute_type) {
            case 2:
                return $this->DecryptPassword($attribute_raw_value, $this->_request_authenticator);
            case 4:
                return inet_ntop($attribute_raw_value);
            case 5:
                return unpack('N', $attribute_raw_value)[1];
            default:
                return $attribute_raw_value;
        }
    }

    public function ClearLastError() {
        $this->_last_error_code = 0;
        $this->_last_error_message = '';
    }

    public function ClearDataToSend() {
        $this->_attributes_to_send = array();
        $this->_radius_packet_to_send = 0;
        $this->_identifier_to_send = 0;
        $this->_request_authenticator = '';
    }

    public function ClearDataReceived() {
        $this->_attributes_received = array();
        $this->_radius_packet_received = 0;
        $this->_identifier_received = 0;
        $this->_response_authenticator = '';
    }

    private function BuildRadiusPacket() {
        $header = chr($this->_radius_packet_to_send) .
                  chr($this->_identifier_to_send) .
                  pack('n', 20) .
                  $this->_request_authenticator;
        $body = '';
        foreach ($this->_attributes_to_send as $type => $value) {
            $length = strlen($value) + 2;
            $body .= chr($type) . chr($length) . $value;
        }
        $total_length = 20 + strlen($body);
        $header = chr($this->_radius_packet_to_send) .
                  chr($this->_identifier_to_send) .
                  pack('n', $total_length) .
                  $this->_request_authenticator;
        return $header . $body;
    }

    private function SendPacketToServer($packet) {
        $port = $this->_radius_packet_to_send == self::RADIUS_ACCESS_REQUEST ? $this->_authentication_port : $this->_accounting_port;
        $this->_socket_to_server = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);
        if (!$this->_socket_to_server) {
            $this->_last_error_code = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
            return false;
        }
        socket_set_option($this->_socket_to_server, SOL_SOCKET, SO_RCVTIMEO, pack("l", $this->_udp_timeout));
        $result = socket_sendto($this->_socket_to_server, $packet, strlen($packet), 0, $this->_ip_radius_server, $port);
        if ($result === false) {
            $this->_last_error_code = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
            return false;
        }
        return true;
    }

    private function ReceivePacketFromServer() {
        $buffer = '';
        $from = '';
        $port = 0;
        $result = socket_recvfrom($this->_socket_to_server, $buffer, 4096, 0, $from, $port);
        if ($result === false) {
            $this->_last_error_code = socket_last_error();
            $this->_last_error_message = socket_strerror($this->_last_error_code);
            return false;
        }
        return $buffer;
    }

    private function ParseAttributes($raw_attributes) {
        $offset = 0;
        while ($offset < strlen($raw_attributes)) {
            $attribute_type = ord(substr($raw_attributes, $offset, 1));
            $attribute_length = ord(substr($raw_attributes, $offset + 1, 1));
            $attribute_value = substr($raw_attributes, $offset + 2, $attribute_length - 2);
            $this->_attributes_received[$attribute_type] = $attribute_value;
            $offset += $attribute_length;
        }
    }

    private function CryptPassword($password, $authenticator) {
        $key = $this->_shared_secret;
        $padlen = 16 - (strlen($password) % 16);
        if ($padlen < 16) {
            $password .= str_repeat(chr(0), $padlen);
        }
        $output = '';
        $block = $authenticator;
        for ($i = 0; $i < strlen($password); $i += 16) {
            $md5 = md5($block . $key, true);
            $output .= $xor = '';
            for ($j = 0; $j < 16; $j++) {
                $xor .= chr(ord($password{$i + $j}) ^ ord($md5{$j}));
            }
            $block = $xor;
        }
        return $output;
    }

    private function DecryptPassword($password, $authenticator) {
        $key = $this->_shared_secret;
        $output = '';
        $block = $authenticator;
        for ($i = 0; $i < strlen($password); $i += 16) {
            $md5 = md5($block . $key, true);
            $output .= $xor = '';
            for ($j = 0; $j < 16; $j++) {
                $xor .= chr(ord($password{$i + $j}) ^ ord($md5{$j}));
            }
            $block = $xor;
        }
        return rtrim($output, "\x00");
    }
