function smarty_function_fetch($params, $template)
{
    if (empty($params['file'])) {
        trigger_error("[plugin] fetch parameter 'file' cannot be empty", E_USER_NOTICE);
        return;
    }

    $content = '';
    if (preg_match('!^http://!i', $params['file'])) {
        if ($uri_parts = parse_url($params['file'])) {
            $host = $server_name = $uri_parts['host'];
            $timeout = 30;
            $port = empty($uri_parts['port']) ? 80 : $uri_parts['port'];
            $user = empty($uri_parts['user']) ? null : $uri_parts['user'];
            $pass = empty($uri_parts['pass']) ? null : $uri_parts['pass'];
            $uri = !empty($uri_parts['path']) ? $uri_parts['path'] : '/';
            $uri .= !empty($uri_parts['query']) ? '?' . $uri_parts['query'] : '';
            $_is_proxy = false;
            $proxy_host = $proxy_port = null;
            $extra_headers = array();
            $accept = "image/gif, image/x-xbitmap, image/jpeg, image/pjpeg, */*";
            $agent = "Smarty Template Engine " . Smarty::SMARTY_VERSION;
            $referer = "";

            foreach ($params as $param_key => $param_value) {
                switch ($param_key) {
                    case "file":
                    case "assign":
                    case "assign_headers":
                        break;
                    case "user":
                        if (!empty($param_value)) $user = $param_value;
                        break;
                    case "pass":
                        if (!empty($param_value)) $pass = $param_value;
                        break;
                    case "accept":
                        if (!empty($param_value)) $accept = $param_value;
                        break;
                    case "header":
                        if (!empty($param_value) && preg_match('![\w\d-]+: .+!', $param_value)) {
                            $extra_headers[] = $param_value;
                        }
                        break;
                    case "proxy_host":
                        if (!empty($param_value)) $proxy_host = $param_value;
                        break;
                    case "proxy_port":
                        if (!preg_match('!\D!', $param_value)) $proxy_port = (int)$param_value;
                        break;
                    case "agent":
                        if (!empty($param_value)) $agent = $param_value;
                        break;
                    case "referer":
                        if (!empty($param_value)) $referer = $param_value;
                        break;
                    case "timeout":
                        if (!preg_match('!\D!', $param_value)) $timeout = (int)$param_value;
                        break;
                }
            }

            if (!empty($proxy_host) && !empty($proxy_port)) {
                $_is_proxy = true;
                $fp = fsockopen($proxy_host, $proxy_port, $errno, $errstr, $timeout);
            } else {
                $fp = fsockopen($server_name, $port, $errno, $errstr, $timeout);
            }

            if (!$fp) {
                trigger_error("[plugin] unable to fetch: $errstr ($errno)", E_USER_NOTICE);
                return;
            }

            if ($_is_proxy) {
                fputs($fp, 'GET ' . $params['file'] . " HTTP/1.0\r\n");
            } else {
                fputs($fp, "GET $uri HTTP/1.0\r\n");
            }
            if (!empty($host)) fputs($fp, "Host: $host\r\n");
            if (!empty($accept)) fputs($fp, "Accept: $accept\r\n");
            if (!empty($agent)) fputs($fp, "User-Agent: $agent\r\n");
            if (!empty($referer)) fputs($fp, "Referer: $referer\r\n");
            foreach ($extra_headers as $curr_header) {
                fputs($fp, $curr_header . "\r\n");
            }
            if (!empty($user) && !empty($pass)) {
                fputs($fp, "Authorization: BASIC " . base64_encode("$user:$pass") . "\r\n");
            }

            fputs($fp, "\r\n");
            while (!feof($fp)) {
                $content .= fgets($fp, 4096);
            }
            fclose($fp);

            $csplit = preg_split("!\r\n\r\n!", $content, 2);
            $content = $csplit[1];

            if (!empty($params['assign_headers'])) {
                $template->assign($params['assign_headers'], preg_split("!\r\n!", $csplit[0]));
            }
        }
    }

    if (!empty($params['assign'])) {
        $template->assign($params['assign'], $content);
    } else {
        return $content;
    }
}

namespace Org\Net;

class Http
{
    /**
     * 使用 fsockopen 通过 HTTP 协议直接访问(采集)远程文件
     * 如果主机或服务器没有开启 CURL 扩展可考虑使用
     * fsockopen 比 CURL 稍慢,但性能稳定
     * @static
     * @access public
     * @param string $url 远程URL
     * @param array $conf 其他配置信息
     *        int   limit 分段读取字符个数
     *        string post  post的内容,字符串或数组,key=value&形式
     *        string cookie 携带cookie访问,该参数是cookie内容
     *        string ip    如果该参数传入,$url将不被使用,ip访问优先
     *        int    timeout 采集超时时间
     *        bool   block 是否阻塞访问,默认为true
     * @return mixed
     */
    static public function fsockopenDownload($url, $conf = array())
    {
        $return = '';
        if(!is_array($conf)) return $return;

        $matches = parse_url($url);
        !isset($matches['host'])    && $matches['host']    = '';
        !isset($matches['path'])    && $matches['path']    = '';
        !isset($matches['query'])   && $matches['query']   = '';
        !isset($matches['port'])     && $matches['port']    = '';
        $host = $matches['host'];
        $path = $matches['path'] ? $matches['path'].($matches['query'] ? '?'.$matches['query'] : '') : '/';
        $port = !empty($matches['port']) ? $matches['port'] : 80;

        $conf_arr = array(
            'limit'     =>  0,
            'post'      =>  '',
            'cookie'    =>  '',
            'ip'        =>  '',
            'timeout'   =>  15,
            'block'     =>  TRUE,
            );

        foreach (array_merge($conf_arr, $conf) as $k=>$v) ${$k} = $v;

        if($post) {
            if(is_array($post))
            {
                $post = http_build_query($post);
            }
            $out  = "POST $path HTTP/1.0\r\n";
            $out .= "Accept: */*\r\n";
            $out .= "Accept-Language: zh-cn\r\n";
            $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
            $out .= "User-Agent: $_SERVER[HTTP_USER_AGENT]\r\n";
            $out .= "Host: $host\r\n";
            $out .= 'Content-Length: '.strlen($post)."\r\n";
            $out .= "Connection: Close\r\n";
            $out .= "Cache-Control: no-cache\r\n";
            $out .= "Cookie: $cookie\r\n\r\n";
            $out .= $post;
        } else {
            $out  = "GET $path HTTP/1.0\r\n";
            $out .= "Accept: */*\r\n";
            $out .= "Accept-Language: zh-cn\r\n";
            $out .= "User-Agent: $_SERVER[HTTP_USER_AGENT]\r\n";
            $out .= "Host: $host\r\n";
            $out .= "Connection: Close\r\n";
            $out .= "Cookie: $cookie\r\n\r\n";
        }
        $fp = @fsockopen(($ip ? $ip : $host), $port, $errno, $errstr, $timeout);
        if(!$fp) {
            return '';
        } else {
            stream_set_blocking($fp, $block);
            stream_set_timeout($fp, $timeout);
            @fwrite($fp, $out);
            $status = stream_get_meta_data($fp);
            if(!$status['timed_out']) {
                while (!feof($fp)) {
                    if(($header = @fgets($fp)) && ($header == "\r\n" ||  $header == "\n")) {
                        break;
                    }
                }

                $stop = false;
                while(!feof($fp) && !$stop) {
                    $data = fread($fp, ($limit == 0 || $limit > 8192 ? 8192 : $limit));
                    $return .= $data;
                    if($limit) {
                        $limit -= strlen($data);
                        $stop = $limit <= 0;
                    }
                }
            }
            @fclose($fp);
            return $return;
        }
    }
}

