function getGitInformation(string $path)
{
    if (!\is_dir($path . DIRECTORY_SEPARATOR . '.git')) {
        return false;
    }

    $process = \proc_open(
        'git describe --tags',
        [
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w'],
        ],
        $pipes,
        $path
    );

    if (!\is_resource($process)) {
        return false;
    }

    $result = \trim(\stream_get_contents($pipes[1]));

    \fclose($pipes[1]);
    \fclose($pipes[2]);

    $returnCode = \proc_close($process);

    if ($returnCode !== 0) {
        return false;
    }

    return $result;
}

use function array_merge;
use function fclose;
use function file_put_contents;
use function fread;
